import { useState } from 'react'
import { Note } from '@/types/note'
import { NotesList } from '@/components/NotesList'
import { NoteEditor } from '@/components/NoteEditor'
import EmptyState from '@/components/EmptyState'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { useNotes } from '@/hooks/use-notes'
import { ArrowLeft, Plus, Search } from 'lucide-react'

export default function NotesPage() {
  const { notes, loading, error, createNote, updateNote, deleteNote } = useNotes()
  const [editingNote, setEditingNote] = useState<Note | null>(null)
  const [isCreateOpen, setIsCreateOpen] = useState(false)
  const [searchQuery, setSearchQuery] = useState('')
  const [colorFilter, setColorFilter] = useState<string>('all')
  const [sortBy, setSortBy] = useState<'updated' | 'created' | 'title'>('updated')

  const filteredNotes = notes
    .filter(note => 
      note.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      note.content.toLowerCase().includes(searchQuery.toLowerCase())
    )
    .filter(note => colorFilter === 'all' || note.color === colorFilter)
    .sort((a, b) => {
      switch (sortBy) {
        case 'updated':
          return new Date(b.updated_at).getTime() - new Date(a.updated_at).getTime()
        case 'created':
          return new Date(b.created_at).getTime() - new Date(a.created_at).getTime()
        case 'title':
          return a.title.localeCompare(b.title)
        default:
          return 0
      }
    })

  const handleEditNote = (note: Note) => {
    setEditingNote(note)
    setIsCreateOpen(false)
  }

  const handleCreateNote = () => {
    setIsCreateOpen(true)
    setEditingNote(null)
  }

  const handleSaveNote = async (data: Partial<Note>) => {
    try {
      const noteData = {
        title: data.title || 'Untitled Note',
        content: data.content || '',
        color: data.color || 'gray'
      }

      if (editingNote) {
        await updateNote(editingNote.id, noteData)
      } else {
        await createNote(noteData)
      }
      setEditingNote(null)
      setIsCreateOpen(false)
    } catch (error) {
      console.error('Failed to save note:', error)
    }
  }

  const handleCancelEdit = () => {
    setEditingNote(null)
    setIsCreateOpen(false)
  }

  const handleDeleteNote = async (id: string) => {
    try {
      await deleteNote(id)
    } catch (error) {
      console.error('Failed to delete note:', error)
    }
  }

  const colors = ['all', 'blue', 'green', 'yellow', 'red', 'purple', 'gray']

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-gray-900"></div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="text-center py-8">
        <p className="text-red-500">Error loading notes: {error}</p>
      </div>
    )
  }

  if (editingNote || isCreateOpen) {
    return (
      <div className="max-w-4xl mx-auto">
        <div className="flex items-center justify-between mb-6">
          <Button
            variant="ghost"
            onClick={handleCancelEdit}
            className="flex items-center gap-2"
          >
            <ArrowLeft className="h-4 w-4" />
            Back to Notes
          </Button>
        </div>
        <NoteEditor
          note={editingNote}
          onSave={handleSaveNote}
          onCancel={handleCancelEdit}
        />
      </div>
    )
  }

  return (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h1 className="text-3xl font-bold">Notes</h1>
          <p className="text-muted-foreground">Manage your notes</p>
        </div>
        <Button onClick={handleCreateNote} className="flex items-center gap-2">
          <Plus className="h-4 w-4" />
          New Note
        </Button>
      </div>

      <div className="flex flex-col sm:flex-row gap-4">
        <div className="relative flex-1">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
          <Input
            placeholder="Search notes..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10"
          />
        </div>
        <Select value={colorFilter} onValueChange={setColorFilter}>
          <SelectTrigger className="w-full sm:w-[120px]">
            <SelectValue placeholder="Color" />
          </SelectTrigger>
          <SelectContent>
            {colors.map(color => (
              <SelectItem key={color} value={color}>
                {color === 'all' ? 'All Colors' : color}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
        <Select value={sortBy} onValueChange={(value) => setSortBy(value as any)}>
          <SelectTrigger className="w-full sm:w-[140px]">
            <SelectValue placeholder="Sort by" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="updated">Updated</SelectItem>
            <SelectItem value="created">Created</SelectItem>
            <SelectItem value="title">Title</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {filteredNotes.length === 0 ? (
        <EmptyState onCreateNote={handleCreateNote} />
      ) : (
        <NotesList
          notes={filteredNotes}
          onDelete={handleDeleteNote}
          onUpdate={updateNote}
          onEdit={handleEditNote}
        />
      )}
    </div>
  )
}

