import { useState, useEffect, useRef } from 'react'
import { ChevronLeft, Plus, Trash2, GripVertical, Hash, List, ListOrdered, CheckSquare, Code, Quote, Type, Heading1, Heading2, Heading3 } from 'lucide-react'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { useIsMobile } from '@/hooks/use-mobile'
import { cn } from '@/lib/utils'
import { Note, CreateNoteData } from '@/types/note'
import { ScrollArea } from '@/components/ui/scroll-area'
import { Separator } from '@/components/ui/separator'
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover'
import { Command, CommandEmpty, CommandGroup, CommandInput, CommandItem, CommandList } from '@/components/ui/command'

interface Block {
  id: string
  type: 'text' | 'heading1' | 'heading2' | 'heading3' | 'bullet' | 'numbered' | 'todo' | 'code' | 'quote'
  content: string
  checked?: boolean
}

interface NoteEditorProps {
  note: Note
  onSave: (data: Partial<Note> | CreateNoteData) => void
  onCancel: () => void
}

export function NoteEditor({ note, onSave, onCancel }: NoteEditorProps) {
  const [title, setTitle] = useState(note.title || 'Untitled')
  const [blocks, setBlocks] = useState<Block[]>(() => {
    try {
      const parsed = JSON.parse(note.content || '[]')
      return Array.isArray(parsed) ? parsed : [{ id: '1', type: 'text', content: note.content || '' }]
    } catch {
      return [{ id: '1', type: 'text', content: note.content || '' }]
    }
  })
  const [showSlashMenu, setShowSlashMenu] = useState(false)
  const [slashMenuPosition, setSlashMenuPosition] = useState({ x: 0, y: 0 })
  const [activeBlockId, setActiveBlockId] = useState<string | null>(null)
  const isMobile = useIsMobile()

  const blockTypes = [
    { type: 'text', label: 'Text', icon: Type },
    { type: 'heading1', label: 'Heading 1', icon: Heading1 },
    { type: 'heading2', label: 'Heading 2', icon: Heading2 },
    { type: 'heading3', label: 'Heading 3', icon: Heading3 },
    { type: 'bullet', label: 'Bulleted List', icon: List },
    { type: 'numbered', label: 'Numbered List', icon: ListOrdered },
    { type: 'todo', label: 'To-do List', icon: CheckSquare },
    { type: 'code', label: 'Code Block', icon: Code },
    { type: 'quote', label: 'Quote', icon: Quote },
  ]

  const addBlock = (type: Block['type'], index?: number) => {
    const newBlock: Block = {
      id: Date.now().toString(),
      type,
      content: '',
      ...(type === 'todo' && { checked: false })
    }

    if (index !== undefined) {
      setBlocks(prev => [...prev.slice(0, index + 1), newBlock, ...prev.slice(index + 1)])
    } else {
      setBlocks(prev => [...prev, newBlock])
    }
  }

  const updateBlock = (id: string, updates: Partial<Block>) => {
    setBlocks(prev => prev.map(block => 
      block.id === id ? { ...block, ...updates } : block
    ))
  }

  const deleteBlock = (id: string) => {
    setBlocks(prev => prev.filter(block => block.id !== id))
  }

  const handleKeyDown = (e: React.KeyboardEvent, blockId: string) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault()
      const currentIndex = blocks.findIndex(b => b.id === blockId)
      addBlock('text', currentIndex)
    }

    if (e.key === 'Backspace' && e.currentTarget.textContent === '') {
      e.preventDefault()
      deleteBlock(blockId)
    }

    if (e.key === '/') {
      const selection = window.getSelection()
      if (selection && selection.rangeCount > 0) {
        const range = selection.getRangeAt(0)
        const rect = range.getBoundingClientRect()
        setSlashMenuPosition({ x: rect.left, y: rect.bottom })
        setShowSlashMenu(true)
      }
    }
  }

  const handleSlashCommand = (type: Block['type']) => {
    const currentIndex = blocks.findIndex(b => b.id === activeBlockId)
    updateBlock(activeBlockId!, { type, content: '' })
    setShowSlashMenu(false)
  }

  const renderBlock = (block: Block) => {
    const baseClasses = "min-h-[1.5em] outline-none focus:outline-none focus:ring-0 px-2 py-1 rounded hover:bg-gray-50 dark:hover:bg-gray-800 transition-colors"

    switch (block.type) {
      case 'heading1':
        return (
          <h1 
            className={`${baseClasses} text-3xl font-bold`}
            contentEditable
            suppressContentEditableWarning
            onBlur={(e) => updateBlock(block.id, { content: e.currentTarget.textContent || '' })}
            onKeyDown={(e) => handleKeyDown(e, block.id)}
          >
            {block.content}
          </h1>
        )
      case 'heading2':
        return (
          <h2 
            className={`${baseClasses} text-2xl font-semibold`}
            contentEditable
            suppressContentEditableWarning
            onBlur={(e) => updateBlock(block.id, { content: e.currentTarget.textContent || '' })}
            onKeyDown={(e) => handleKeyDown(e, block.id)}
          >
            {block.content}
          </h2>
        )
      case 'heading3':
        return (
          <h3 
            className={`${baseClasses} text-xl font-medium`}
            contentEditable
            suppressContentEditableWarning
            onBlur={(e) => updateBlock(block.id, { content: e.currentTarget.textContent || '' })}
            onKeyDown={(e) => handleKeyDown(e, block.id)}
          >
            {block.content}
          </h3>
        )
      case 'bullet':
        return (
          <div className="flex items-start gap-2">
            <span className="text-gray-500 mt-1">‚Ä¢</span>
            <div 
              className={`${baseClasses} flex-1`}
              contentEditable
              suppressContentEditableWarning
              onBlur={(e) => updateBlock(block.id, { content: e.currentTarget.textContent || '' })}
              onKeyDown={(e) => handleKeyDown(e, block.id)}
            >
              {block.content}
            </div>
          </div>
        )
      case 'numbered':
        return (
          <div className="flex items-start gap-2">
            <span className="text-gray-500 mt-1">{blocks.filter(b => b.type === 'numbered').indexOf(block) + 1}.</span>
            <div 
              className={`${baseClasses} flex-1`}
              contentEditable
              suppressContentEditableWarning
              onBlur={(e) => updateBlock(block.id, { content: e.currentTarget.textContent || '' })}
              onKeyDown={(e) => handleKeyDown(e, block.id)}
            >
              {block.content}
            </div>
          </div>
        )
      case 'todo':
        return (
          <div className="flex items-start gap-2">
            <input
              type="checkbox"
              checked={block.checked}
              onChange={(e) => updateBlock(block.id, { checked: e.target.checked })}
              className="mt-1"
            />
            <div 
              className={`${baseClasses} flex-1 ${block.checked ? 'line-through text-gray-500' : ''}`}
              contentEditable
              suppressContentEditableWarning
              onBlur={(e) => updateBlock(block.id, { content: e.currentTarget.textContent || '' })}
              onKeyDown={(e) => handleKeyDown(e, block.id)}
            >
              {block.content}
            </div>
          </div>
        )
      case 'code':
        return (
          <pre className={`${baseClasses} bg-gray-100 dark:bg-gray-800 rounded-md p-3 font-mono text-sm`}>
            <code 
              contentEditable
              suppressContentEditableWarning
              onBlur={(e) => updateBlock(block.id, { content: e.currentTarget.textContent || '' })}
              onKeyDown={(e) => handleKeyDown(e, block.id)}
            >
              {block.content}
            </code>
          </pre>
        )
      case 'quote':
        return (
          <blockquote 
            className={`${baseClasses} border-l-4 border-gray-300 pl-4 italic text-gray-600`}
            contentEditable
            suppressContentEditableWarning
            onBlur={(e) => updateBlock(block.id, { content: e.currentTarget.textContent || '' })}
            onKeyDown={(e) => handleKeyDown(e, block.id)}
          >
            {block.content}
          </blockquote>
        )
      default:
        return (
          <div 
            className={`${baseClasses}`}
            contentEditable
            suppressContentEditableWarning
            onBlur={(e) => updateBlock(block.id, { content: e.currentTarget.textContent || '' })}
            onKeyDown={(e) => handleKeyDown(e, block.id)}
            onFocus={() => setActiveBlockId(block.id)}
          >
            {block.content}
          </div>
        )
    }
  }

  const handleSave = () => {
    onSave({ 
      title, 
      content: JSON.stringify(blocks) 
    })
  }

  return (
    <div className={cn("flex flex-col h-full", isMobile ? "" : "")}>
      {/* Header */}
      <div className={cn(
        "flex items-center justify-between border-b px-4 py-3",
        isMobile ? "px-4 py-3" : "px-6 py-4"
      )}>
        <div className="flex items-center gap-3">
          <Button
            variant="ghost"
            size="sm"
            onClick={onCancel}
            className="hover:bg-gray-100 dark:hover:bg-gray-800"
          >
            <ChevronLeft className="h-4 w-4" />
            {!isMobile && "Back"}
          </Button>
          <Input
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className={cn(
              "border-none p-0 text-xl font-semibold bg-transparent focus:outline-none focus:ring-0",
              isMobile ? "text-lg" : "text-xl"
            )}
            placeholder="Untitled"
          />
        </div>
        <Button onClick={handleSave} size={isMobile ? "sm" : "default"}>
          Save
        </Button>
      </div>

      {/* Content */}
      <ScrollArea className="flex-1 px-4 py-6">
        <div className={cn(
          "max-w-3xl mx-auto space-y-2",
          isMobile ? "px-4" : "px-0"
        )}>
          {blocks.map((block, index) => (
            <div key={block.id} className="group relative">
              {renderBlock(block)}
              <Button
                variant="ghost"
                size="sm"
                className="absolute -left-8 top-0 opacity-0 group-hover:opacity-100 transition-opacity"
                onClick={() => addBlock('text', index)}
              >
                <Plus className="h-3 w-3" />
              </Button>
            </div>
          ))}
          <Button
            variant="ghost"
            size="sm"
            onClick={() => addBlock('text')}
            className="w-full justify-start text-gray-500 hover:text-gray-700"
          >
            <Plus className="h-4 w-4 mr-2" />
            Add a block
          </Button>
        </div>
      </ScrollArea>

      {/* Slash Menu */}
      <Popover open={showSlashMenu} onOpenChange={setShowSlashMenu}>
        <PopoverContent 
          className="w-64 p-0"
          style={{
            position: 'fixed',
            left: slashMenuPosition.x,
            top: slashMenuPosition.y,
          }}
        >
          <Command>
            <CommandInput placeholder="Type a command..." />
            <CommandList>
              <CommandEmpty>No results found.</CommandEmpty>
              <CommandGroup>
                {blockTypes.map((type) => (
                  <CommandItem
                    key={type.type}
                    onSelect={() => handleSlashCommand(type.type as Block['type'])}
                  >
                    <type.icon className="h-4 w-4 mr-2" />
                    {type.label}
                  </CommandItem>
                ))}
              </CommandGroup>
