import { useState } from 'react';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Button } from '@/components/ui/button';
import { Note, CreateNoteData, UpdateNoteData } from '@/types/note';

interface NoteFormProps {
  note?: Note;
  onSubmit: (data: CreateNoteData | UpdateNoteData) => void;
  onCancel: () => void;
  loading?: boolean;
}

export default function NoteForm({ note, onSubmit, onCancel, loading }: NoteFormProps) {
  const [title, setTitle] = useState(note?.title || '');
  const [content, setContent] = useState(note?.content || '');
  const [color, setColor] = useState(note?.color || '#ffffff');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const data = {
      title: title.trim(),
      content: content.trim(),
      color,
    };

    onSubmit(data);
  };

  const colors = ['#ffffff', '#fef3c7', '#dbeafe', '#d1fae5', '#fce7f3', '#e9d5ff'];

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div className="space-y-2">
        <Label htmlFor="title">Title</Label>
        <Input
          id="title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Enter note title..."
          maxLength={200}
          required
        />
      </div>

      <div className="space-y-2">
        <Label htmlFor="content">Content</Label>
        <Textarea
          id="content"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          placeholder="Write your note here..."
          rows={6}
          required
        />
      </div>

      <div className="space-y-2">
        <Label>Color</Label>
        <div className="flex gap-2">
          {colors.map((c) => (
            <button
              key={c}
              type="button"
              className={`w-8 h-8 rounded-full border-2 transition-all ${
                color === c ? 'border-primary scale-110' : 'border-transparent'
              }`}
              style={{ backgroundColor: c }}
              onClick={() => setColor(c)}
            />
          ))}
        </div>
      </div>

      <div className="flex gap-2 justify-end">
        <Button type="button" variant="outline" onClick={onCancel}>
          Cancel
        </Button>
        <Button type="submit" disabled={loading || !title.trim() || !content.trim()}>
          {loading ? 'Saving...' : note ? 'Update' : 'Create'}
        </Button>
      </div>
    </form>
  );
}

