import { useState } from 'react'
import { Note } from '@/types/note'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Badge } from '@/components/ui/badge'
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from '@/components/ui/dropdown-menu'
import { MoreVertical, Trash2, Edit3, Palette } from 'lucide-react'
import { cn } from '@/lib/utils'
import { formatDistanceToNow } from 'date-fns'
import { ColorPicker } from './ColorPicker'

interface NoteCardProps {
  note: Note
  onDelete: (id: string) => Promise<void>
  onUpdate: (id: string, data: any) => Promise<Note>
  onEdit: (note: Note) => void
}

export function NoteCard({ note, onDelete, onUpdate, onEdit }: NoteCardProps) {
  const [showColorPicker, setShowColorPicker] = useState(false)
  const [isDeleting, setIsDeleting] = useState(false)

  const handleDelete = async () => {
    setIsDeleting(true)
    try {
      await onDelete(note.id)
    } catch (error) {
      console.error('Failed to delete note:', error)
    } finally {
      setIsDeleting(false)
    }
  }

  const handleColorChange = async (color: string) => {
    try {
      await onUpdate(note.id, { color })
      setShowColorPicker(false)
    } catch (error) {
      console.error('Failed to update color:', error)
    }
  }

  const getColorClasses = (color: string) => {
    const colors: Record<string, string> = {
      blue: 'border-blue-200 bg-blue-50 hover:border-blue-300',
      green: 'border-green-200 bg-green-50 hover:border-green-300',
      yellow: 'border-yellow-200 bg-yellow-50 hover:border-yellow-300',
      red: 'border-red-200 bg-red-50 hover:border-red-300',
      purple: 'border-purple-200 bg-purple-50 hover:border-purple-300',
      gray: 'border-gray-200 bg-gray-50 hover:border-gray-300',
    }
    return colors[color] || colors.gray
  }

  const getBadgeColor = (color: string) => {
    const colors: Record<string, string> = {
      blue: 'bg-blue-100 text-blue-800',
      green: 'bg-green-100 text-green-800',
      yellow: 'bg-yellow-100 text-yellow-800',
      red: 'bg-red-100 text-red-800',
      purple: 'bg-purple-100 text-purple-800',
      gray: 'bg-gray-100 text-gray-800',
    }
    return colors[color] || colors.gray
  }

  return (
    <Card
      className={cn(
        'group relative cursor-pointer transition-all duration-200 hover:shadow-md',
        getColorClasses(note.color)
      )}
      onClick={() => onEdit(note)}
    >
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <CardTitle className="text-lg font-semibold line-clamp-2">
            {note.title || 'Untitled Note'}
          </CardTitle>
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button
                variant="ghost"
                size="sm"
                className="h-8 w-8 p-0 opacity-0 group-hover:opacity-100 transition-opacity"
                onClick={(e) => e.stopPropagation()}
              >
                <MoreVertical className="h-4 w-4" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              <DropdownMenuItem onClick={(e) => { e.stopPropagation(); onEdit(note) }}>
                <Edit3 className="mr-2 h-4 w-4" />
                Edit
              </DropdownMenuItem>
              <DropdownMenuItem onClick={(e) => { e.stopPropagation(); setShowColorPicker(true) }}>
                <Palette className="mr-2 h-4 w-4" />
                Change Color
              </DropdownMenuItem>
              <DropdownMenuItem
                className="text-destructive"
                onClick={(e) => { e.stopPropagation(); handleDelete() }}
                disabled={isDeleting}
              >
                <Trash2 className="mr-2 h-4 w-4" />
                {isDeleting ? 'Deleting...' : 'Delete'}
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </CardHeader>
      <CardContent>
        <p className="text-sm text-muted-foreground line-clamp-3 mb-3">
          {note.content || 'No content'}
        </p>
        <div className="flex items-center justify-between">
          <Badge className={cn('text-xs', getBadgeColor(note.color))}>
            {note.color}
          </Badge>
          <span className="text-xs text-muted-foreground">
            {formatDistanceToNow(new Date(note.updated_at), { addSuffix: true })}
          </span>
        </div>
      </CardContent>

      {showColorPicker && (
        <ColorPicker
          currentColor={note.color}
          onColorChange={handleColorChange}
          onClose={() => setShowColorPicker(false)}
        />
      )}
    </Card>
  )
}

