      setContact(data);
      setFormData(data); // Initialize form with current contact data
    } catch (err: any) {
      setError(err.message || 'Failed to fetch contact.');
      toast({
        title: 'Error',
        description: err.message || 'Failed to fetch contact.',
        variant: 'destructive',
      });
