```xml
<action type="file" filePath="frontend/src/components/crm/TagInput.tsx">
import React, { useState, useEffect } from 'react';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { X, PlusCircle, Loader2 } from 'lucide-react';
import { Tag, TagCreate } from '@/types/crm';
import { crmApi } from '@/services/crm_api';
import { useToast } from '@/components/ui/use-toast';

interface TagInputProps {
  entityId: string; // ID of the entity (contact/lead) this tag is associated with
  currentTags: Tag[]; // Tags currently associated with the entity
  onTagsUpdated: (newTags: Tag[]) => void; // Callback when tags are updated
}

const TagInput: React.FC<TagInputProps> = ({ entityId, currentTags, onTagsUpdated }) => {
  const [inputValue, setInputValue] = useState('');
  const [allAvailableTags, setAllAvailableTags] = useState<Tag[]>([]);
  const [loading, setLoading] = useState(false);
  const { toast } = useToast();

  useEffect(() => {
    fetchAllTags();
  }, []);

  const fetchAllTags = async () => {
    setLoading(true);
    try {
      const tags = await crmApi.getTags();
      setAllAvailableTags(tags);
    } catch (err: any) {
      toast({
        title: 'Error',
        description: err.message || 'Failed to fetch tags.',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  const handleAddTag = async () => {
    if (inputValue.trim() === '') return;

    const tagName = inputValue.trim();
    const existingTag = allAvailableTags.find(tag => tag.name.toLowerCase() === tagName.toLowerCase());

    try {
      let newTag: Tag;
      if (existingTag) {
        newTag = existingTag;
      } else {
        // Create new tag if it doesn't exist
        const created = await crmApi.createTag({ name: tagName });
        newTag = created;
        setAllAvailableTags(prev => [...prev, newTag]);
      }

      // Check if tag is already associated with this entity (mock for now)
      // In a real app, you'd have a many-to-many relationship table
      if (!currentTags.some(tag => tag.id === newTag.id)) {
        const updatedTags = [...currentTags, newTag];
        onTagsUpdated(updatedTags);
        toast({ title: 'Success', description: `Tag '${newTag.name}' added.` });
      } else {
        toast({ title: 'Info', description: `Tag '${newTag.name}' is already associated.` });
      }
      setInputValue('');
    } catch (err: any) {
      toast({
        title: 'Error',
        description: err.message || 'Failed to add tag.',
        variant: 'destructive',
      });
    }
  };

  const handleRemoveTag = async (tagId: string) => {
    // In a real app, you'd remove the association from the many-to-many table
    const updatedTags = currentTags.filter(tag => tag.id !== tagId);
    onTagsUpdated(updatedTags);
    toast({ title: 'Success', description: 'Tag removed.' });
  };

  return (
    <div className="space-y-3">
      <div className="flex gap-2">
        <Input
          placeholder="Add new tag..."
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyPress={(e) => {
            if (e.key === 'Enter') {
              e.preventDefault();
              handleAddTag();
            }
          }}
          className="bg-input/50 border-blue-700/50 focus-visible:ring-blue-400"
        />
        <Button onClick={handleAddTag} className="bg-blue-700 hover:bg-blue-600 text-primary-foreground">
          {loading ? <Loader2 className="h-4 w-4 animate-spin" /> : <PlusCircle className="h-4 w-4" />}
        </Button>
      </div>
      <div className="flex flex-wrap gap-2">
        {currentTags.map(tag => (
          <Badge key={tag.id} className="bg-blue-500 text-white hover:bg-blue-600 cursor-pointer">
            {tag.name}
            <X className="ml-1 h-3 w-3" onClick={() => handleRemoveTag(tag.id)} />
          </Badge>
        ))}
      </div>
    </div>
  );
};

export default TagInput;
</action>
```

