import React, { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { DialogFooter } from '@/components/ui/dialog';
import { Contact, ContactCreate, ContactUpdate } from '@/types/crm';
import { Loader2 } from 'lucide-react';

interface ContactFormProps {
  initialContact?: Contact;
  onSubmit: (data: ContactCreate | ContactUpdate) => Promise<void>;
  onCancel: () => void;
  isSubmitting: boolean;
}

const ContactForm: React.FC<ContactFormProps> = ({ initialContact, onSubmit, onCancel, isSubmitting }) => {
  const [formData, setFormData] = useState<ContactCreate | ContactUpdate>({});

  useEffect(() => {
    if (initialContact) {
      setFormData(initialContact);
    } else {
      setFormData({});
    }
  }, [initialContact]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { id, value } = e.target;
    setFormData(prev => ({ ...prev, [id]: value }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    await onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="grid gap-4 py-4">
        <div className="grid gap-2">
          <Label htmlFor="first_name">First Name</Label>
          <Input
            id="first_name"
            placeholder="John"
            value={formData.first_name || ''}
            onChange={handleChange}
            required
            className="bg-input/50 border-blue-700/50 focus-visible:ring-blue-400"
          />
        </div>
        <div className="grid gap-2">
          <Label htmlFor="last_name">Last Name</Label>
          <Input
            id="last_name"
            placeholder="Doe"
            value={formData.last_name || ''}
            onChange={handleChange}
            className="bg-input/50 border-blue-700/50 focus-visible:ring-blue-400"
          />
        </div>
        <div className="grid gap-2">
          <Label htmlFor="email">Email</Label>
          <Input
            id="email"
            type="email"
            placeholder="john.doe@example.com"
            value={formData.email || ''}
            onChange={handleChange}
            className="bg-input/50 border-blue-700/50 focus-visible:ring-blue-400"
          />
        </div>
        <div className="grid gap-2">
          <Label htmlFor="phone">Phone</Label>
          <Input
            id="phone"
            placeholder="123-456-7890"
            value={formData.phone || ''}
            onChange={handleChange}
            className="bg-input/50 border-blue-700/50 focus-visible:ring-blue-400"
          />
        </div>
        <div className="grid gap-2">
          <Label htmlFor="company">Company</Label>
          <Input
            id="company"
            placeholder="Acme Corp"
            value={formData.company || ''}
            onChange={handleChange}
            className="bg-input/50 border-blue-700/50 focus-visible:ring-blue-400"
          />
        </div>
      </div>
      <DialogFooter>
        <Button variant="ghost" onClick={onCancel} disabled={isSubmitting} className="text-muted-foreground hover:bg-secondary/80">
          Cancel
        </Button>
        <Button type="submit" disabled={isSubmitting} className="bg-blue-700 hover:bg-blue-600 text-primary-foreground">
          {isSubmitting ? <Loader2 className="mr-2 h-4 w-4 animate-spin" /> : null}
          {initialContact ? 'Save Changes' : 'Create Contact'}
        </Button>
      </DialogFooter>
    </form>
  );
};

export default ContactForm;
</action>
