<action type="file" filePath="frontend/src/components/crm/LeadForm.tsx">
import React, { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import { DialogFooter } from '@/components/ui/dialog';
import { Lead, LeadCreate, LeadUpdate } from '@/types/crm';
import { LeadStatus, LeadSource } from '@/types/enums';
import { Loader2 } from 'lucide-react';

interface LeadFormProps {
  initialLead?: Lead;
  onSubmit: (data: LeadCreate | LeadUpdate) => Promise<void>;
  onCancel: () => void;
  isSubmitting: boolean;
}

const LeadForm: React.FC<LeadFormProps> = ({ initialLead, onSubmit, onCancel, isSubmitting }) => {
  const [formData, setFormData] = useState<LeadCreate | LeadUpdate>({});

  useEffect(() => {
    if (initialLead) {
      setFormData(initialLead);
    } else {
      setFormData({
        status: LeadStatus.NEW, // Default status
        source: LeadSource.OTHER, // Default source
      });
    }
  }, [initialLead]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { id, value } = e.target;
    setFormData(prev => ({ ...prev, [id]: value }));
  };

  const handleSelectChange = (id: string, value: string) => {
    setFormData(prev => ({ ...prev, [id]: value }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    await onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="grid gap-4 py-4">
        <div className="grid gap-2">
          <Label htmlFor="name">Lead Name</Label>
          <Input
            id="name"
            placeholder="New Sales Opportunity"
            value={formData.name || ''}
            onChange={handleChange}
            required
            className="bg-input/50 border-blue-700/50 focus-visible:ring-blue-400"
          />
        </div>
        <div className="grid gap-2">
          <Label htmlFor="email">Email</Label>
          <Input
            id="email"
            type="email"
            placeholder="lead@example.com"
            value={formData.email || ''}
            onChange={handleChange}
            className="bg-input/50 border-blue-700/50 focus-visible:ring-blue-400"
          />
        </div>
