          onChange={(e) => setNewPassword(e.target.value)}
          required
          className="bg-input/50 border-blue-700/50 focus-visible:ring-blue-400"
        />
      </div>
      <div className="grid gap-2">
        <Label htmlFor="confirm-new-password">Confirm New Password</Label>
        <Input
