<action type="file" filePath="frontend/src/services/leads.ts">
import { API_BASE_URL } from '../config';
import { Lead, LeadCreate, LeadUpdate, PaginatedResponse } from '../types/crm';

const getToken = (): string => {
  const token = localStorage.getItem('access_token');
  if (!token) {
    throw new Error('No access token found');
  }
  return token;
};

export const createLead = async (leadData: LeadCreate): Promise<Lead> => {
  const response = await fetch(`${API_BASE_URL}/leads/`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${getToken()}`,
    },
    body: JSON.stringify(leadData),
  });

  if (!response.ok) {
    const errorData = await response.json();
    throw new Error(errorData.detail || 'Failed to create lead');
  }
  return response.json();
};

export const getLeads = async (page: number = 1, limit: number = 10): Promise<PaginatedResponse<Lead>> => {
  const response = await fetch(`${API_BASE_URL}/leads/?skip=${(page - 1) * limit}&limit=${limit}`, {
    method: 'GET',
    headers: {
      'Authorization': `Bearer ${getToken()}`,
    },
  });

  if (!response.ok) {
    const errorData = await response.json();
    throw new Error(errorData.detail || 'Failed to fetch leads');
  }
  return response.json();
};

export const getLeadById = async (id: string): Promise<Lead> => {
  const response = await fetch(`${API_BASE_URL}/leads/${id}/`, {
    method: 'GET',
    headers: {
      'Authorization': `Bearer ${getToken()}`,
    },
  });

  if (!response.ok) {
    const errorData = await response.json();
    throw new Error(errorData.detail || 'Failed to fetch lead');
  }
  return response.json();
};

export const updateLead = async (id: string, leadData: LeadUpdate): Promise<Lead> => {
  const response = await fetch(`${API_BASE_URL}/leads/${id}/`, {
    method: 'PUT',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${getToken()}`,
    },
    body: JSON.stringify(leadData),
  });

  if (!response.ok) {
    const errorData = await response.json();
    throw new Error(errorData.detail || 'Failed to update lead');
  }
  return response.json();
};

export const deleteLead = async (id: string): Promise<void> => {
  const response = await fetch(`${API_BASE_URL}/leads/${id}/`, {
    method: 'DELETE',
    headers: {
      'Authorization': `Bearer ${getToken()}`,
    },
  });

  if (!response.ok) {
    const errorData = await response.json();
    throw new Error(errorData.detail || 'Failed to delete lead');
  }
};
</action>
