import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { z } from 'zod';
import { zodResolver } from '@hookform/resolvers/zod';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { LeadCreate, LeadUpdate, Lead, LeadStatus } from '@/types/crm';

// Define the schema for lead form validation
const leadFormSchema = z.object({
  first_name: z.string().min(1, 'First name is required'),
  last_name: z.string().optional(),
  email: z.string().email('Invalid email address').optional().or(z.literal('')),
  phone: z.string().optional(),
  company: z.string().optional(),
  title: z.string().optional(),
  source: z.string().optional(),
  status: z.nativeEnum(LeadStatus),
});

type LeadFormData = z.infer<typeof leadFormSchema>;

interface LeadFormProps {
  initialData?: Lead;
  onSubmit: (data: LeadCreate | LeadUpdate) => void;
  isLoading?: boolean;
}

const LeadForm: React.FC<LeadFormProps> = ({ initialData, onSubmit, isLoading }) => {
  const { register, handleSubmit, reset, setValue, formState: { errors } } = useForm<LeadFormData>({
    resolver: zodResolver(leadFormSchema),
    defaultValues: initialData || {
      first_name: '',
      last_name: '',
      email: '',
      phone: '',
      company: '',
      title: '',
      source: '',
      status: LeadStatus.NEW, // Default status
    },
  });

  useEffect(() => {
    if (initialData) {
      reset(initialData);
      setValue('status', initialData.status); // Set select value explicitly
    }
  }, [initialData, reset, setValue]);

  const handleFormSubmit = (data: LeadFormData) => {
    // Filter out empty strings for optional fields to send undefined/null
    const cleanedData: LeadCreate | LeadUpdate = {
      ...data,
      email: data.email || undefined,
      last_name: data.last_name || undefined,
      phone: data.phone || undefined,
      company: data.company || undefined,
      title: data.title || undefined,
      source: data.source || undefined,
    };
    onSubmit(cleanedData);
  };

  return (
    <Card className="bg-blue-950 text-blue-100 border-blue-800 shadow-lg">
      <CardHeader>
        <CardTitle className="text-blue-300">{initialData ? 'Edit Lead' : 'Create New Lead'}</CardTitle>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit(handleFormSubmit)} className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div className="grid gap-2">
            <Label htmlFor="first_name">First Name</Label>
            <Input id="first_name" {...register('first_name')} className="bg-blue-900 border-blue-700 text-white placeholder:text-blue-400 focus:ring-blue-500" />
            {errors.first_name && <p className="text-red-400 text-sm">{errors.first_name.message}</p>}
          </div>
          <div className="grid gap-2">
            <Label htmlFor="last_name">Last Name</Label>
            <Input id="last_name" {...register('last_name')} className="bg-blue-900 border-blue-700 text-white placeholder:text-blue-400 focus:ring-blue-500" />
          </div>
          <div className="grid gap-2">
            <Label htmlFor="email">Email</Label>
            <Input id="email" type="email" {...register('email')} className="bg-blue-900 border-blue-700 text-white placeholder:text-blue-400 focus:ring-blue-500" />
            {errors.email && <p className="text-red-400 text-sm">{errors.email.message}</p>}
          </div>
          <div className="grid gap-2">
            <Label htmlFor="phone">Phone</Label>
            <Input id="phone" {...register('phone')} className="bg-blue-900 border-blue-700 text-white placeholder:text-blue-400 focus:ring-blue-500" />
          </div>
          <div className="grid gap-2">
            <Label htmlFor="company">Company</Label>
            <Input id="company" {...register('company')} className="bg-blue-900 border-blue-700 text-white placeholder:text-blue-400 focus:ring-blue-500" />
          </div>
          <div className="grid gap-2">
            <Label htmlFor="title">Title</Label>
            <Input id="title" {...register('title')} className="bg-blue-900 border-blue-700 text-white placeholder:text-blue-400 focus:ring-blue-500" />
          </div>
          <div className="grid gap-2">
            <Label htmlFor="source">Source</Label>
            <Input id="source" {...register('source')} className="bg-blue-900 border-blue-700 text-white placeholder:text-blue-400 focus:ring-blue-500" />
          </div>
          <div className="grid gap-2">
            <Label htmlFor="status">Status</Label>
            <Select onValueChange={(value) => setValue('status', value as LeadStatus)} value={initialData?.status || LeadStatus.NEW}>
              <SelectTrigger className="bg-blue-900 border-blue-700 text-white focus:ring-blue-500">
                <SelectValue placeholder="Select a status" />
              </SelectTrigger>
              <SelectContent className="bg-blue-900 text-white border-blue-700">
                {Object.values(LeadStatus).map((status) => (
                  <SelectItem key={status} value={status}>
                    {status}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            {errors.status && <p className="text-red-400 text-sm">{errors.status.message}</p>}
