import { API_BASE_URL } from '../config';
import { Note, Contact, Lead } from '../types/crm';

const getToken = (): string => {
  const token = localStorage.getItem('access_token');
  if (!token) {
    throw new Error('No access token found');
  }
  return token;
};

export const addNoteToEntity = async (entityType: 'contact' | 'lead', entityId: string, noteContent: string): Promise<Contact | Lead> => {
  const response = await fetch(`${API_BASE_URL}/${entityType}s/${entityId}/notes/`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${getToken()}`,
    },
    body: JSON.stringify({ content: noteContent }),
  });

  if (!response.ok) {
    const errorData = await response.json();
    throw new Error(errorData.detail || `Failed to add note to ${entityType}`);
  }
  return response.json();
};
</action>
