import { API_BASE_URL } from '../config';
import { Tag, Contact, Lead } from '../types/crm';

const getToken = (): string => {
  const token = localStorage.getItem('access_token');
  if (!token) {
    throw new Error('No access token found');
  }
  return token;
};

export const addTagToEntity = async (entityType: 'contact' | 'lead', entityId: string, tagName: string, tagColor?: string): Promise<Contact | Lead> => {
  const response = await fetch(`${API_BASE_URL}/${entityType}s/${entityId}/tags/`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${getToken()}`,
    },
    body: JSON.stringify({ name: tagName, color: tagColor }),
  });

  if (!response.ok) {
    const errorData = await response.json();
    throw new Error(errorData.detail || `Failed to add tag to ${entityType}`);
  }
  return response.json();
};

export const removeTagFromEntity = async (entityType: 'contact' | 'lead', entityId: string, tagName: string): Promise<Contact | Lead> => {
  const response = await fetch(`${API_BASE_URL}/${entityType}s/${entityId}/tags/${tagName}/`, {
    method: 'DELETE',
    headers: {
      'Authorization': `Bearer ${getToken()}`,
    },
  });

  if (!response.ok) {
    const errorData = await response.json();
    throw new Error(errorData.detail || `Failed to remove tag from ${entityType}`);
  }
  return response.json();
};
</action>
