      setNewNoteContent('');
      toast({
        title: 'Success',
        description: 'Note added successfully.',
      });
    } catch (error: any) {
      toast({
        title: 'Error',
        description: error.message || 'Failed to add note.',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <Card className="bg-blue-950 text-blue-100 border-blue-800 shadow-lg">
