<action type="file" filePath="frontend/src/components/SearchFilterBar.tsx">
import React, { useState, useEffect } from 'react';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { LeadStatus } from '@/types/crm'; // Only needed for Lead filters

interface SearchFilterBarProps {
  onSearchChange: (searchTerm: string) => void;
  onFilterChange: (filterValue: string) => void;
  filterOptions?: { value: string; label: string }[];
  filterPlaceholder?: string;
  currentSearch?: string;
  currentFilter?: string;
}

const SearchFilterBar: React.FC<SearchFilterBarProps> = ({
  onSearchChange,
  onFilterChange,
  filterOptions,
  filterPlaceholder,
  currentSearch = '',
  currentFilter = '',
}) => {
  const [searchTerm, setSearchTerm] = useState(currentSearch);
  const [filterValue, setFilterValue] = useState(currentFilter);

  useEffect(() => {
    setSearchTerm(currentSearch);
  }, [currentSearch]);

  useEffect(() => {
    setFilterValue(currentFilter);
  }, [currentFilter]);

  const handleSearchInput = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
    onSearchChange(e.target.value);
  };

  const handleFilterSelect = (value: string) => {
    setFilterValue(value);
    onFilterChange(value);
  };

  return (
    <div className="flex flex-col sm:flex-row gap-4 mb-6">
      <Input
        placeholder="Search by name, email, or company..."
        value={searchTerm}
        onChange={handleSearchInput}
        className="flex-1 bg-blue-900 border-blue-700 text-white placeholder:text-blue-400 focus:ring-blue-500"
      />
      {filterOptions && filterOptions.length > 0 && (
        <Select onValueChange={handleFilterSelect} value={filterValue}>
          <SelectTrigger className="w-[180px] bg-blue-900 border-blue-700 text-white focus:ring-blue-500">
            <SelectValue placeholder={filterPlaceholder || "Filter..."} />
          </SelectTrigger>
          <SelectContent className="bg-blue-900 text-white border-blue-700">
            {filterOptions.map((option) => (
              <SelectItem key={option.value} value={option.value}>
                {option.label}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      )}
    </div>
  );
};

export default SearchFilterBar;
</action>
```

