import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { PlusCircle, Edit, Trash2 } from 'lucide-react';
import { getLeads, deleteLead, createLead } from '@/services/leads';
import { Lead, LeadCreate, LeadStatus } from '@/types/crm';
import { useToast } from '@/components/ui/use-toast';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import LeadForm from '@/components/LeadForm';
import SearchFilterBar from '@/components/SearchFilterBar'; // Import SearchFilterBar

export default function LeadsPage() {
  const [leads, setLeads] = useState<Lead[]>([]);
  const [loading, setLoading] = useState(true);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState<LeadStatus | ''>(''); // For status filter
  const { toast } = useToast();

  const fetchLeads = useCallback(async () => {
    setLoading(true);
    try {
      const response = await getLeads(1, 100, searchTerm, filterStatus); // Pass search and filter
      setLeads(response.items);
    } catch (error: any) {
      toast({
        title: 'Error',
        description: error.message || 'Failed to fetch leads.',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  }, [searchTerm, filterStatus, toast]); // Add dependencies

  useEffect(() => {
    fetchLeads();
  }, [fetchLeads]);

  const handleDelete = async (id: string) => {
    if (window.confirm('Are you sure you want to delete this lead?')) {
      try {
        await deleteLead(id);
        toast({
          title: 'Success',
          description: 'Lead deleted successfully.',
        });
        fetchLeads(); // Refresh the list
      } catch (error: any) {
        toast({
          title: 'Error',
          description: error.message || 'Failed to delete lead.',
          variant: 'destructive',
        });
      }
