      await createLead(data);
      toast({
        title: 'Success',
        description: 'Lead created successfully.',
      });
      setIsDialogOpen(false); // Close the dialog
      fetchLeads(); // Refresh the list
    } catch (error: any) {
      toast({
        title: 'Error',
        description: error.message || 'Failed to create lead.',
        variant: 'destructive',
      });
    }
  };

  // Options for Lead Status filter
  const statusFilterOptions = [
    { value: '', label: 'All Statuses' },
    ...Object.values(LeadStatus).map(status => ({ value: status, label: status }))
  ];

  if (loading) {
    return <div className="text-blue-200">Loading leads...</div>;
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h1 className="text-3xl font-bold tracking-tight text-blue-300">Leads</h1>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
