import { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getLead, updateLead, deleteLead } from '@/services/leads';
import { LeadResponse, LeadUpdate } from '@/types/api';
import { toast } from 'sonner';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import LeadForm from '@/components/LeadForm';
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from '@/components/ui/alert-dialog';
import { Separator } from '@/components/ui/separator';
import { format } from 'date-fns';

function LeadDetailPage() {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [lead, setLead] = useState<LeadResponse | null>(null);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (id) {
      const fetchLead = async () => {
        try {
          const data = await getLead(id);
          setLead(data);
        } catch (err: any) {
          setError(err.message || 'Failed to fetch lead.');
          toast.error(err.message || 'Failed to fetch lead.');
        } finally {
          setLoading(false);
        }
      };
      fetchLead();
    }
  }, [id]);

  const handleUpdateLead = async (data: LeadUpdate) => {
    if (!id) return;
    setSubmitting(true);
    try {
      const updated = await updateLead(id, data);
      setLead(updated);
      toast.success('Lead updated successfully!');
    } catch (err: any) {
      toast.error(err.message || 'Failed to update lead.');
    } finally {
      setSubmitting(false);
    }
  };

  const handleDeleteLead = async () => {
    if (!id) return;
    try {
      await deleteLead(id);
      toast.success('Lead deleted successfully!');
      navigate('/leads');
    } catch (err: any) {
      toast.error(err.message || 'Failed to delete lead.');
    }
  };

  if (loading) {
    return <div className="text-center">Loading lead details...</div>;
  }

  if (error) {
    return <div className="text-center text-red-500">Error: {error}</div>;
  }

  if (!lead) {
    return <div className="text-center">Lead not found.</div>;
  }

  return (
    <div className="flex flex-col gap-4">
      <div className="flex items-center justify-between">
        <h1 className="text-3xl font-bold">
          {lead.company_name} {lead.contact_person ? `(${lead.contact_person})` : ''}
        </h1>
        <AlertDialog>
          <AlertDialogTrigger asChild>
            <Button variant="destructive">Delete Lead</Button>
          </AlertDialogTrigger>
          <AlertDialogContent>
            <AlertDialogHeader>
              <AlertDialogTitle>Are you absolutely sure?</AlertDialogTitle>
              <AlertDialogDescription>
                This action cannot be undone. This will permanently delete this lead.
              </AlertDialogDescription>
            </AlertDialogHeader>
            <AlertDialogFooter>
              <AlertDialogCancel>Cancel</AlertDialogCancel>
              <AlertDialogAction onClick={handleDeleteLead}>Delete</AlertDialogAction>
            </AlertDialogFooter>
          </AlertDialogContent>
        </AlertDialog>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Edit Lead</CardTitle>
          <CardDescription>Update lead information.</CardDescription>
        </CardHeader>
        <CardContent>
          <LeadForm initialData={lead} onSubmit={handleUpdateLead} isLoading={submitting} />
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle>Notes</CardTitle>
          <CardDescription>Notes related to this lead.</CardDescription>
        </CardHeader>
