import { UserCreate, UserLogin, User, Token } from '../types/api';

const API_URL = import.meta.env.VITE_API_URL || 'http://localhost:8002';

const AUTH_TOKEN_KEY = 'authToken';

export const saveToken = (token: string) => {
  localStorage.setItem(AUTH_TOKEN_KEY, token);
};

export const getToken = (): string | null => {
  return localStorage.getItem(AUTH_TOKEN_KEY);
};

export const removeToken = () => {
  localStorage.removeItem(AUTH_TOKEN_KEY);
};

export const fetchWithAuth = async (url: string, options: RequestInit = {}): Promise<Response> => {
  const token = getToken();
  const headers = {
    ...options.headers,
    'Content-Type': 'application/json',
  };

  if (token) {
    headers['Authorization'] = `Bearer ${token}`;
  }

  const response = await fetch(`${API_URL}${url}`, {
    ...options,
    headers,
  });

  if (response.status === 401) {
    // Token expired or invalid, remove it and redirect to login
    removeToken();
    window.location.href = '/login';
  }

  return response;
};

export const registerUser = async (userData: UserCreate): Promise<User> => {
  const response = await fetchWithAuth('/api/auth/register/', {
    method: 'POST',
    body: JSON.stringify(userData),
  });

  if (!response.ok) {
    const errorData = await response.json();
    throw new Error(errorData.detail || 'Registration failed');
  }
  return response.json();
};

export const loginUser = async (credentials: UserLogin): Promise<Token> => {
  const formBody = new URLSearchParams();
  formBody.append('username', credentials.username);
  formBody.append('password', credentials.password);

  const response = await fetch(`${API_URL}/api/auth/token/`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded',
    },
    body: formBody.toString(),
  });

  if (!response.ok) {
    const errorData = await response.json();
    throw new Error(errorData.detail || 'Login failed');
  }
  const data: Token = await response.json();
  saveToken(data.access_token);
  return data;
};

export const fetchCurrentUser = async (): Promise<User> => {
  const response = await fetchWithAuth('/api/auth/me/', {
    method: 'GET',
  });

  if (!response.ok) {
    const errorData = await response.json();
    throw new Error(errorData.detail || 'Failed to fetch current user');
  }
  return response.json();
};
