import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { getContact, createContact, updateContact, deleteContact } from '@/services/contacts';
import { Contact, ContactCreate, ContactUpdate } from '@/types/api';
import { LoadingSpinner } from '@/components/ui/loading-spinner';
import { ErrorMessage } from '@/components/ui/error-message';
import { ContactForm } from '@/components/ContactForm';
import { useToast } from '@/components/ui/use-toast';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";

export default function ContactDetailPage() {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const { toast } = useToast();

  const [contact, setContact] = useState<Contact | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const isNewContact = id === 'new';

  useEffect(() => {
    if (!isNewContact) {
      const fetchContact = async () => {
        try {
          const data = await getContact(id!);
          setContact(data);
        } catch (err) {
          setError(err instanceof Error ? err.message : 'Failed to load contact.');
        } finally {
          setIsLoading(false);
        }
      };
      fetchContact();
    } else {
      setIsLoading(false);
    }
  }, [id, isNewContact]);

  const handleSubmit = async (data: ContactCreate | ContactUpdate) => {
    setIsSubmitting(true);
    setError(null);
    try {
      if (isNewContact) {
        await createContact(data as ContactCreate);
        toast({ title: 'Success', description: 'Contact created successfully.' });
        navigate('/contacts');
      } else {
        await updateContact(id!, data as ContactUpdate);
        toast({ title: 'Success', description: 'Contact updated successfully.' });
        // Re-fetch contact to update UI with latest data
        const updatedContact = await getContact(id!);
        setContact(updatedContact);
      }
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'An unknown error occurred.';
      setError(errorMessage);
      toast({ title: 'Error', description: errorMessage, variant: 'destructive' });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleDelete = async () => {
    setIsSubmitting(true);
    setError(null);
    try {
      await deleteContact(id!);
      toast({ title: 'Success', description: 'Contact deleted successfully.' });
      navigate('/contacts');
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'An unknown error occurred.';
      setError(errorMessage);
      toast({ title: 'Error', description: errorMessage, variant: 'destructive' });
    } finally {
      setIsSubmitting(false);
    }
  };

  if (isLoading) {
    return (
      <div className="flex justify-center items-center h-64">
        <LoadingSpinner />
      </div>
    );
  }

  if (error && !isNewContact) {
    return <ErrorMessage message={error} />;
  }

  return (
    <div className="flex-1 space-y-4 p-8 pt-6">
      <div className="flex items-center justify-between space-y-2">
        <h2 className="text-3xl font-bold tracking-tight">
          {isNewContact ? 'Create New Contact' : 'Contact Details'}
        </h2>
        {!isNewContact && (
          <AlertDialog>
            <AlertDialogTrigger asChild>
              <Button variant="destructive" disabled={isSubmitting}>Delete Contact</Button>
            </AlertDialogTrigger>
            <AlertDialogContent>
              <AlertDialogHeader>
                <AlertDialogTitle>Are you absolutely sure?</AlertDialogTitle>
                <AlertDialogDescription>
                  This action cannot be undone. This will permanently delete this contact.
                </AlertDialogDescription>
              </AlertDialogHeader>
              <AlertDialogFooter>
                <AlertDialogCancel>Cancel</AlertDialogCancel>
                <AlertDialogAction onClick={handleDelete} disabled={isSubmitting}>
                  {isSubmitting ? <LoadingSpinner /> : 'Delete'}
                </AlertDialogAction>
              </AlertDialogFooter>
            </AlertDialogContent>
          </AlertDialog>
        )}
      </div>
      <Card>
        <CardHeader>
          <CardTitle>{isNewContact ? 'New Contact Information' : `${contact?.first_name} ${contact?.last_name}`}</CardTitle>
          <CardDescription>{isNewContact ? 'Fill in the details to create a new contact.' : 'View and edit contact details.'}</CardDescription>
        </CardHeader>
        <CardContent>
          <ContactForm
            initialData={contact || undefined}
            onSubmit={handleSubmit}
            isLoading={isSubmitting}
          />
        </CardContent>
      </Card>

      {!isNewContact && (
        <>
          <Card>
            <CardHeader>
              <CardTitle>Notes</CardTitle>
              <CardDescription>Notes related to this contact.</CardDescription>
            </CardHeader>
            <CardContent>
              {contact?.notes && contact.notes.length > 0 ? (
                <ul>
                  {contact.notes.map((note) => (
                    <li key={note.id} className="mb-2 border-b pb-2 last:border-b-0">
                      <p>{note.content}</p>
                      <p className="text-xs text-muted-foreground">
                        {new Date(note.created_at).toLocaleString()}
                      </p>
                    </li>
                  ))}
                </ul>
              ) : (
                <p className="text-muted-foreground">No notes for this contact yet.</p>
              )}
              {/* TODO: Add form to add new notes */}
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Tags</CardTitle>
              <CardDescription>Tags associated with this contact.</CardDescription>
            </CardHeader>
            <CardContent>
              {contact?.tags && contact.tags.length > 0 ? (
                <div className="flex flex-wrap gap-2">
                  {contact.tags.map((tag) => (
                    <span
                      key={tag.id}
                      className="inline-flex items-center rounded-md px-2.5 py-0.5 text-xs font-semibold"
                      style={{ backgroundColor: tag.color, color: 'white' }}
                    >
                      {tag.name}
                    </span>
                  ))}
                </div>
              ) : (
                <p className="text-muted-foreground">No tags for this contact yet.</p>
              )}
              {/* TODO: Add form to add new tags */}
            </CardContent>
          </Card>
        </>
      )}
    </div>
  );
}
