<action type="file" filePath="frontend/src/pages/LeadDetailPage.tsx">
import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { getLead, createLead, updateLead, deleteLead } from '@/services/leads';
import { Lead, LeadCreate, LeadUpdate } from '@/types/api';
import { LoadingSpinner } from '@/components/ui/loading-spinner';
import { ErrorMessage } from '@/components/ui/error-message';
import { LeadForm } from '@/components/LeadForm';
import { useToast } from '@/components/ui/use-toast';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";

export default function LeadDetailPage() {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const { toast } = useToast();

  const [lead, setLead] = useState<Lead | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const isNewLead = id === 'new';

  useEffect(() => {
    if (!isNewLead) {
      const fetchLead = async () => {
        try {
          const data = await getLead(id!);
          setLead(data);
        } catch (err) {
          setError(err instanceof Error ? err.message : 'Failed to load lead.');
        } finally {
          setIsLoading(false);
        }
      };
      fetchLead();
    } else {
      setIsLoading(false);
    }
  }, [id, isNewLead]);

  const handleSubmit = async (data: LeadCreate | LeadUpdate) => {
    setIsSubmitting(true);
    setError(null);
    try {
      if (isNewLead) {
        await createLead(data as LeadCreate);
        toast({ title: 'Success', description: 'Lead created successfully.' });
        navigate('/leads');
      } else {
        await updateLead(id!, data as LeadUpdate);
        toast({ title: 'Success', description: 'Lead updated successfully.' });
        // Re-fetch lead to update UI with latest data
        const updatedLead = await getLead(id!);
        setLead(updatedLead);
      }
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'An unknown error occurred.';
      setError(errorMessage);
      toast({ title: 'Error', description: errorMessage, variant: 'destructive' });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleDelete = async () => {
    setIsSubmitting(true);
    setError(null);
    try {
      await deleteLead(id!);
      toast({ title: 'Success', description: 'Lead deleted successfully.' });
      navigate('/leads');
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'An unknown error occurred.';
      setError(errorMessage);
      toast({ title: 'Error', description: errorMessage, variant: 'destructive' });
    } finally {
      setIsSubmitting(false);
    }
  };

  if (isLoading) {
    return (
      <div className="flex justify-center items-center h-64">
        <LoadingSpinner />
      </div>
    );
  }

  if (error && !isNewLead) {
    return <ErrorMessage message={error} />;
  }

  return (
    <div className="flex-1 space-y-4 p-8 pt-6">
      <div className="flex items-center justify-between space-y-2">
        <h2 className="text-3xl font-bold tracking-tight">
          {isNewLead ? 'Create New Lead' : 'Lead Details'}
        </h2>
        {!isNewLead && (
          <AlertDialog>
            <AlertDialogTrigger asChild>
              <Button variant="destructive" disabled={isSubmitting}>Delete Lead</Button>
            </AlertDialogTrigger>
            <AlertDialogContent>
              <AlertDialogHeader>
                <AlertDialogTitle>Are you absolutely sure?</AlertDialogTitle>
                <AlertDialogDescription>
                  This action cannot be undone. This will permanently delete this lead.
                </AlertDialogDescription>
              </AlertDialogHeader>
              <AlertDialogFooter>
                <AlertDialogCancel>Cancel</AlertDialogCancel>
                <AlertDialogAction onClick={handleDelete} disabled={isSubmitting}>
                  {isSubmitting ? <LoadingSpinner /> : 'Delete'}
                </AlertDialogAction>
              </AlertDialogFooter>
            </AlertDialogContent>
          </AlertDialog>
        )}
      </div>
      <Card>
        <CardHeader>
          <CardTitle>{isNewLead ? 'New Lead Information' : `${lead?.name}`}</CardTitle>
          <CardDescription>{isNewLead ? 'Fill in the details to create a new lead.' : 'View and edit lead details.'}</CardDescription>
        </CardHeader>
        <CardContent>
          <LeadForm
            initialData={lead || undefined}
            onSubmit={handleSubmit}
            isLoading={isSubmitting}
          />
        </CardContent>
      </Card>

      {!isNewLead && (
        <>
          <Card>
            <CardHeader>
              <CardTitle>Notes</CardTitle>
              <CardDescription>Notes related to this lead.</CardDescription>
            </CardHeader>
            <CardContent>
              {lead?.notes && lead.notes.length > 0 ? (
                <ul>
                  {lead.notes.map((note) => (
                    <li key={note.id} className="mb-2 border-b pb-2 last:border-b-0">
                      <p>{note.content}</p>
                      <p className="text-xs text-muted-foreground">
                        {new Date(note.created_at).toLocaleString()}
                      </p>
                    </li>
                  ))}
                </ul>
              ) : (
                <p className="text-muted-foreground">No notes for this lead yet.</p>
              )}
              {/* TODO: Add form to add new notes */}
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Tags</CardTitle>
              <CardDescription>Tags associated with this lead.</CardDescription>
            </CardHeader>
            <CardContent>
              {lead?.tags && lead.tags.length > 0 ? (
                <div className="flex flex-wrap gap-2">
                  {lead.tags.map((tag) => (
                    <span
                      key={tag.id}
                      className="inline-flex items-center rounded-md px-2.5 py-0.5 text-xs font-semibold"
                      style={{ backgroundColor: tag.color, color: 'white' }}
                    >
                      {tag.name}
                    </span>
                  ))}
                </div>
              ) : (
                <p className="text-muted-foreground">No tags for this lead yet.</p>
              )}
              {/* TODO: Add form to add new tags */}
            </CardContent>
          </Card>
        </>
      )}
    </div>
  );
}
</action>

