import React, { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from '@/components/ui/card';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { AlertCircle } from 'lucide-react';

interface AuthFormProps {
  type: 'login' | 'signup' | 'reset';
  onSubmit: (data: any) => void;
  loading: boolean;
  error: string | null;
}

export function AuthForm({ type, onSubmit, loading, error }: AuthFormProps) {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    first_name: '',
    last_name: '',
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setFormData({
      ...formData,
      [e.target.id]: e.target.value,
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(formData);
  };

  const getTitle = () => {
    switch (type) {
      case 'login': return 'Login to your account';
      case 'signup': return 'Create an account';
      case 'reset': return 'Reset your password';
    }
  };

  const getDescription = () => {
    switch (type) {
      case 'login': return 'Enter your credentials to access your account';
      case 'signup': return 'Enter your information to create an account';
      case 'reset': return 'Enter your email to receive password reset instructions';
    }
  };

  const getButtonText = () => {
    switch (type) {
      case 'login': return 'Login';
      case 'signup': return 'Sign Up';
      case 'reset': return 'Send Reset Instructions';
    }
  };

  return (
    <Card className="w-full max-w-md">
      <CardHeader>
        <CardTitle>{getTitle()}</CardTitle>
        <CardDescription>{getDescription()}</CardDescription>
      </CardHeader>
      <CardContent>
        {error && (
          <Alert variant="destructive" className="mb-4">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>{error}</AlertDescription>
          </Alert>
        )}
        <form onSubmit={handleSubmit}>
          {type !== 'reset' && (
            <>
              <div className="grid grid-cols-2 gap-4 mb-4">
                <div className="space-y-2">
                  <Label htmlFor="first_name">First Name</Label>
                  <Input
                    id="first_name"
                    value={formData.first_name}
                    onChange={handleChange}
                    required={type === 'signup'}
                    disabled={loading || type === 'login'}
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="last_name">Last Name</Label>
                  <Input
                    id="last_name"
                    value={formData.last_name}
                    onChange={handleChange}
                    required={type === 'signup'}
                    disabled={loading || type === 'login'}
                  />
                </div>
              </div>
            </>
          )}
          <div className="space-y-2 mb-4">
            <Label htmlFor="email">Email</Label>
            <Input
              id="email"
              type="email"
              value={formData.email}
              onChange={handleChange}
              required
              disabled={loading}
            />
          </div>
          {type !== 'reset' && (
            <div className="space-y-2 mb-4">
              <Label htmlFor="password">Password</Label>
              <Input
                id="password"
                type="password"
                value={formData.password}
                onChange={handleChange}
                required
                disabled={loading}
              />
            </div>
          )}
          <Button type="submit" className="w-full" disabled={loading}>
            {loading ? 'Processing...' : getButtonText()}
          </Button>
        </form>
      </CardContent>
      <CardFooter className="flex flex-col">
        {type === 'login' && (
          <div className="text-sm text-center">
            <a href="/reset-password" className="text-blue-600 hover:underline">
              Forgot your password?
            </a>
          </div>
        )}
        {type === 'signup' && (
          <div className="text-sm text-center">
            Already have an account?{' '}
            <a href="/login" className="text-blue-600 hover:underline">
              Login
            </a>
          </div>
        )}
        {type === 'reset' && (
          <div className="text-sm text-center">
            Remember your password?{' '}
            <a href="/login" className="text-blue-600 hover:underline">
              Login
            </a>
          </div>
        )}
        {type === 'login' && (
          <div className="text-sm text-center mt-2">
            Don't have an account?{' '}
            <a href="/signup" className="text-blue-600 hover:underline">
              Sign up
            </a>
          </div>
        )}
      </CardFooter>
    </Card>
  );
}
