import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { Button } from '@/components/ui/button'
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog'
import { ContactList } from '@/components/ContactList'
import { ContactForm } from '@/components/ContactForm'
import { ContactDetail } from '@/components/ContactDetail'
import { contactService } from '@/services/contactService'
import { Contact, ContactCreate, ContactUpdate } from '@/types/contact'
import { Plus, User } from 'lucide-react'

export default function ContactsPage() {
  const navigate = useNavigate()
  const [selectedContact, setSelectedContact] = useState<Contact | null>(null)
  const [isFormOpen, setIsFormOpen] = useState(false)
  const [editingContact, setEditingContact] = useState<Contact | null>(null)
  const [isSubmitting, setIsSubmitting] = useState(false)

  const handleCreateContact = () => {
    setEditingContact(null)
    setIsFormOpen(true)
  }

  const handleEditContact = (contact: Contact) => {
    setEditingContact(contact)
    setIsFormOpen(true)
  }

  const handleViewContact = (contact: Contact) => {
    setSelectedContact(contact)
  }

  const handleDeleteContact = async (id: string) => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      try {
        await contactService.deleteContact(id)
        // Refresh the contact list
        window.location.reload()
      } catch (error) {
        console.error('Error deleting contact:', error)
        alert('Failed to delete contact')
      }
    }
  }

  const handleSaveContact = async (data: ContactCreate | ContactUpdate) => {
    setIsSubmitting(true)
    try {
      if (editingContact) {
        // Update existing contact
        await contactService.updateContact(editingContact.id, data as ContactUpdate)
      } else {
        // Create new contact
        await contactService.createContact(data as ContactCreate)
      }

      setIsFormOpen(false)
      setEditingContact(null)
      // Refresh the contact list
      window.location.reload()
    } catch (error) {
      console.error('Error saving contact:', error)
      alert('Failed to save contact')
    } finally {
      setIsSubmitting(false)
    }
  }

  const handleRefresh = () => {
    handleCreateContact()
  }

  return (
    <div className="container mx-auto py-6 space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold">Contacts</h1>
          <p className="text-muted-foreground">Manage your contacts and relationships</p>
        </div>
        <Button onClick={handleCreateContact}>
          <Plus className="mr-2 h-4 w-4" />
          Add Contact
        </Button>
      </div>

      {selectedContact ? (
        <ContactDetail 
          contact={selectedContact} 
          onEdit={() => {
            handleEditContact(selectedContact)
            setSelectedContact(null)
          }} 
        />
      ) : (
        <ContactList 
          onEdit={handleEditContact}
          onDelete={handleDeleteContact}
          onRefresh={handleRefresh}
        />
      )}

      <Dialog open={isFormOpen} onOpenChange={setIsFormOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>
              {editingContact ? 'Edit Contact' : 'Create New Contact'}
            </DialogTitle>
          </DialogHeader>
          <ContactForm
            contact={editingContact || undefined}
            onSubmit={handleSaveContact}
            onCancel={() => setIsFormOpen(false)}
            isSubmitting={isSubmitting}
          />
        </DialogContent>
      </Dialog>
    </div>
  )
}
</action>
