import React, { useEffect, useState } from "react";
import {
  Box, Heading, SimpleGrid, Stat, StatLabel, StatNumber, StatHelpText, StatArrow,
  useColorModeValue, VStack, HStack, Badge, Icon, Spinner, Text
} from "@chakra-ui/react";
import { AtSignIcon, StarIcon, CalendarIcon, EmailIcon } from "@chakra-ui/icons";
import { getContacts, getTags, getCampaigns } from "../api/crm_api";
import { Contact, Tag, Campaign } from "../types/crm_types";

const DashboardPage: React.FC = () => {
  const [contacts, setContacts] = useState<Contact[]>([]);
  const [tags, setTags] = useState<Tag[]>([]);
  const [campaigns, setCampaigns] = useState<Campaign[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    Promise.all([getContacts(), getTags(), getCampaigns()]).then(([contacts, tags, campaigns]) => {
      setContacts(contacts);
      setTags(tags);
      setCampaigns(campaigns);
      setLoading(false);
    });
  }, []);

  const statBg = useColorModeValue("white", "gray.800");

  return (
    <Box p={8}>
      <Heading mb={8} color="teal.700">CRM Dashboard</Heading>
      {loading ? (
        <Spinner size="xl" color="teal.500" />
      ) : (
        <SimpleGrid columns={{ base: 1, md: 3 }} spacing={8} mb={8}>
          <Stat bg={statBg} rounded="md" boxShadow="md" p={6}>
            <StatLabel fontWeight="bold">Contacts</StatLabel>
            <StatNumber color="teal.600">{contacts.length}</StatNumber>
            <StatHelpText>
              <StatArrow type="increase" />
              Active contacts
            </StatHelpText>
            <Icon as={AtSignIcon} boxSize={8} color="teal.400" mt={2} />
          </Stat>
          <Stat bg={statBg} rounded="md" boxShadow="md" p={6}>
            <StatLabel fontWeight="bold">Tags</StatLabel>
            <StatNumber color="purple.600">{tags.length}</StatNumber>
            <StatHelpText>
              <StatArrow type="increase" />
              Used for segmentation
            </StatHelpText>
            <Icon as={StarIcon} boxSize={8} color="purple.400" mt={2} />
          </Stat>
          <Stat bg={statBg} rounded="md" boxShadow="md" p={6}>
            <StatLabel fontWeight="bold">Campaigns</StatLabel>
            <StatNumber color="blue.600">{campaigns.length}</StatNumber>
            <StatHelpText>
              <StatArrow type="increase" />
              Email campaigns
            </StatHelpText>
            <Icon as={CalendarIcon} boxSize={8} color="blue.400" mt={2} />
          </Stat>
        </SimpleGrid>
      )}
      <VStack align="stretch" spacing={6}>
        <Box>
          <Heading size="md" mb={2} color="gray.700">Recent Campaigns</Heading>
          <SimpleGrid columns={{ base: 1, md: 2 }} spacing={4}>
            {campaigns.slice(0, 4).map(campaign => (
              <Box key={campaign.id} p={4} bg="white" rounded="md" boxShadow="sm">
                <HStack justify="space-between">
                  <Box>
                    <Text fontWeight="bold">{campaign.name}</Text>
                    <Text color="gray.600">{campaign.subject}</Text>
                    <Badge colorScheme="blue" mt={2}>Campaign</Badge>
                  </Box>
                  <Icon as={EmailIcon} boxSize={6} color="blue.400" />
                </HStack>
              </Box>
            ))}
            {campaigns.length === 0 && (
              <Text color="gray.400">No campaigns yet.</Text>
            )}
          </SimpleGrid>
        </Box>
        <Box>
          <Heading size="md" mb={2} color="gray.700">Top Tags</Heading>
          <HStack spacing={2}>
            {tags.slice(0, 6).map(tag => (
              <Badge key={tag.id} colorScheme="purple" fontSize="md">{tag.name}</Badge>
            ))}
            {tags.length === 0 && (
              <Text color="gray.400">No tags yet.</Text>
            )}
          </HStack>
        </Box>
      </VStack>
    </Box>
  );
};

export default DashboardPage;
</action>

