import React, { useEffect, useState } from "react";
import {
  getContacts, createContact, deleteContact, getTags
} from "../api/crm_api";
import { Contact, Tag } from "../types/crm_types";

const COLORS = ["bg-primary", "bg-purple-600", "bg-blue-600", "bg-green-600", "bg-orange-500"];

const ContactsPage: React.FC = () => {
  const [contacts, setContacts] = useState<Contact[]>([]);
  const [tags, setTags] = useState<Tag[]>([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [selectedTags, setSelectedTags] = useState<string[]>([]);
  const [loading, setLoading] = useState(true);
  const [adding, setAdding] = useState(false);
  const [showModal, setShowModal] = useState(false);

  const fetchContacts = async () => {
    setLoading(true);
    try {
      const data = await getContacts();
      setContacts(data);
    } catch {}
    setLoading(false);
  };

  const fetchTags = async () => {
    try {
      const data = await getTags();
      setTags(data);
    } catch {}
  };

  useEffect(() => {
    fetchContacts();
    fetchTags();
  }, []);

  const handleAddContact = async () => {
    if (!name || !email) return;
    setAdding(true);
    try {
      await createContact({ name, email, tag_ids: selectedTags.map(id => Number(id)) });
      setName("");
      setEmail("");
      setSelectedTags([]);
      fetchContacts();
      setShowModal(false);
    } catch {}
    setAdding(false);
  };

  const handleDeleteContact = async (id: number) => {
    try {
      await deleteContact(id);
      fetchContacts();
    } catch {}
  };

  return (
    <div className="p-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-primary">Contacts</h1>
        <button
          className="bg-primary text-white px-4 py-2 rounded-lg shadow hover:bg-primary/90 transition"
          onClick={() => setShowModal(true)}
        >
          Add Contact
        </button>
      </div>
      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/40">
          <div className="bg-card rounded-xl shadow-lg p-8 w-full max-w-md">
            <h2 className="text-xl font-semibold mb-6 text-primary">Add New Contact</h2>
            <div className="flex flex-col gap-4">
              <input
                className="input input-bordered"
                value={name}
                onChange={e => setName(e.target.value)}
                placeholder="Full Name"
              />
              <input
                className="input input-bordered"
                value={email}
                onChange={e => setEmail(e.target.value)}
                placeholder="Email"
              />
              <select
                multiple
                className="input input-bordered"
                value={selectedTags}
                onChange={e =>
                  setSelectedTags(Array.from(e.target.selectedOptions, option => option.value))
                }
              >
                {tags.map(tag => (
                  <option key={tag.id} value={tag.id.toString()}>{tag.name}</option>
                ))}
              </select>
              <div className="flex gap-2 mt-4">
                <button
                  className="bg-primary text-white px-4 py-2 rounded-lg shadow hover:bg-primary/90 transition"
                  onClick={handleAddContact}
                  disabled={adding}
                >
                  {adding ? "Adding..." : "Add Contact"}
                </button>
                <button
                  className="bg-muted text-primary px-4 py-2 rounded-lg shadow hover:bg-muted/80 transition"
                  onClick={() => setShowModal(false)}
                  disabled={adding}
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
      {/* Table */}
      <div className="bg-card rounded-xl shadow p-6">
        {loading ? (
          <div className="flex justify-center items-center h-40">
            <span className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-primary"></span>
          </div>
        ) : (
          <table className="w-full text-left border-separate border-spacing-y-2">
            <thead>
              <tr className="text-muted-foreground text-sm">
                <th className="px-4 py-2">Name</th>
                <th className="px-4 py-2">Email</th>
                <th className="px-4 py-2">Tags</th>
                <th className="px-4 py-2 text-center">Actions</th>
              </tr>
            </thead>
            <tbody>
              {contacts.length === 0 ? (
                <tr>
                  <td colSpan={4} className="text-center text-muted-foreground py-6">No contacts yet.</td>
                </tr>
              ) : (
                contacts.map(contact => (
                  <tr key={contact.id} className="bg-background rounded-lg">
                    <td className="px-4 py-2 font-semibold">{contact.name}</td>
                    <td className="px-4 py-2">{contact.email}</td>
                    <td className="px-4 py-2">
                      <div className="flex gap-2 flex-wrap">
                        {contact.tags && contact.tags.length > 0 ? contact.tags.map((tag, idx) => (
                          <span key={tag.id} className={`px-2 py-1 rounded-lg text-xs font-medium text-white ${COLORS[idx % COLORS.length]}`}>{tag.name}</span>
                        )) : <span className="text-muted-foreground text-xs">No tags</span>}
                      </div>
                    </td>
                    <td className="px-4 py-2 text-center">
                      <button
                        className="text-red-600 hover:text-red-800 font-bold px-3 py-1 rounded transition"
                        onClick={() => handleDeleteContact(contact.id)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default ContactsPage;
</action>

