const COLORS = ["bg-blue-600", "bg-primary", "bg-purple-600", "bg-green-600", "bg-orange-500"];

const CampaignsPage: React.FC = () => {
  const [campaigns, setCampaigns] = useState<Campaign[]>([]);
  const [tags, setTags] = useState<Tag[]>([]);
  const [name, setName] = useState("");
  const [subject, setSubject] = useState("");
  const [body, setBody] = useState("");
  const [selectedTags, setSelectedTags] = useState<string[]>([]);
  const [loading, setLoading] = useState(true);
  const [adding, setAdding] = useState(false);
  const [showModal, setShowModal] = useState(false);

  const fetchCampaigns = async () => {
    setLoading(true);
    try {
      const data = await getCampaigns();
      setCampaigns(data);
    } catch {}
    setLoading(false);
  };

  const fetchTags = async () => {
    try {
      const data = await getTags();
      setTags(data);
    } catch {}
  };

  useEffect(() => {
    fetchCampaigns();
    fetchTags();
  }, []);

  const handleAddCampaign = async () => {
    if (!name || !subject || !body) return;
    setAdding(true);
    try {
      await createCampaign({
        name,
        subject,
        body,
        tag_ids: selectedTags.map(id => Number(id)),
      });
      setName("");
      setSubject("");
      setBody("");
      setSelectedTags([]);
      fetchCampaigns();
      setShowModal(false);
    } catch {}
    setAdding(false);
  };

  const handleDeleteCampaign = async (id: number) => {
    try {
      await deleteCampaign(id);
      fetchCampaigns();
    } catch {}
  };

  return (
    <div className="p-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-blue-700">Campaigns</h1>
        <button
          className="bg-blue-600 text-white px-4 py-2 rounded-lg shadow hover:bg-blue-700 transition"
          onClick={() => setShowModal(true)}
        >
          Add Campaign
        </button>
      </div>
      {/* Modal */}
      {showModal && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/40">
          <div className="bg-card rounded-xl shadow-lg p-8 w-full max-w-lg">
            <h2 className="text-xl font-semibold mb-6 text-blue-700">Add New Campaign</h2>
            <div className="flex flex-col gap-4">
              <input
                className="input input-bordered"
                value={name}
                onChange={e => setName(e.target.value)}
                placeholder="Campaign Name"
              />
              <input
                className="input input-bordered"
                value={subject}
                onChange={e => setSubject(e.target.value)}
                placeholder="Subject"
              />
              <textarea
                className="input input-bordered"
                value={body}
                onChange={e => setBody(e.target.value)}
                placeholder="Body"
                rows={3}
              />
              <select
                multiple
                className="input input-bordered"
                value={selectedTags}
                onChange={e =>
                  setSelectedTags(Array.from(e.target.selectedOptions, option => option.value))
                }
              >
                {tags.map(tag => (
                  <option key={tag.id} value={tag.id.toString()}>{tag.name}</option>
                ))}
              </select>
              <div className="flex gap-2 mt-4">
                <button
                  className="bg-blue-600 text-white px-4 py-2 rounded-lg shadow hover:bg-blue-700 transition"
                  onClick={handleAddCampaign}
                  disabled={adding}
                >
                  {adding ? "Adding..." : "Add Campaign"}
                </button>
                <button
                  className="bg-muted text-blue-700 px-4 py-2 rounded-lg shadow hover:bg-muted/80 transition"
                  onClick={() => setShowModal(false)}
                  disabled={adding}
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
      {/* Table */}
      <div className="bg-card rounded-xl shadow p-6">
        {loading ? (
          <div className="flex justify-center items-center h-40">
            <span className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-600"></span>
          </div>
        ) : (
          <table className="w-full text-left border-separate border-spacing-y-2">
            <thead>
              <tr className="text-muted-foreground text-sm">
                <th className="px-4 py-2">Name</th>
                <th className="px-4 py-2">Subject</th>
                <th className="px-4 py-2">Tags</th>
                <th className="px-4 py-2 text-center">Actions</th>
              </tr>
            </thead>
            <tbody>
              {campaigns.length === 0 ? (
                <tr>
                  <td colSpan={4} className="text-center text-muted-foreground py-6">No campaigns yet.</td>
                </tr>
              ) : (
                campaigns.map((campaign, idx) => (
                  <tr key={campaign.id} className="bg-background rounded-lg">
                    <td className="px-4 py-2 font-semibold">{campaign.name}</td>
                    <td className="px-4 py-2">{campaign.subject}</td>
                    <td className="px-4 py-2">
                      <div className="flex gap-2 flex-wrap">
                        {campaign.tags && campaign.tags.length > 0 ? campaign.tags.map((tag, tIdx) => (
                          <span key={tag.id} className={`px-2 py-1 rounded-lg text-xs font-medium text-white ${COLORS[tIdx % COLORS.length]}`}>{tag.name}</span>
                        )) : <span className="text-muted-foreground text-xs">No tags</span>}
                      </div>
                    </td>
                    <td className="px-4 py-2 text-center">
                      <button
                        className="text-red-600 hover:text-red-800 font-bold px-3 py-1 rounded transition"
                        onClick={() => handleDeleteCampaign(campaign.id)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default CampaignsPage;
</action>
