Updating `backend/services/__init__.py` to include all CRM service routers: contact, tag, campaign, and campaign_send.

---

<action type="update_file" path="backend/services/__init__.py">
from fastapi import APIRouter

api_router = APIRouter()

# Import health service
try:
    from .health_service import router as health_router
    api_router.include_router(health_router, tags=["health"])
except ImportError:
    pass

# CRM Services
try:
    from .contact_service import router as contact_router
    api_router.include_router(contact_router, tags=["contacts"])
except ImportError as e:
    print(f"Contact service import error: {e}")

try:
    from .tag_service import router as tag_router
    api_router.include_router(tag_router, tags=["tags"])
except ImportError as e:
    print(f"Tag service import error: {e}")

try:
    from .campaign_service import router as campaign_router
    api_router.include_router(campaign_router, tags=["campaigns"])
except ImportError as e:
    print(f"Campaign service import error: {e}")

try:
    from .campaign_send_service import router as campaign_send_router
    api_router.include_router(campaign_send_router, tags=["campaign_sends"])
except ImportError as e:
    print(f"Campaign send service import error: {e}")
</action>

