import { useEffect, useState } from "react";
import { useCRMStore } from "../stores/crmStore";
import { fetchContacts, createContact, deleteContact, fetchTags } from "../api/crmApi";
import { Contact, Tag } from "../types/crm_types";
import { Card } from "../components/ui/card";
import { Button } from "../components/ui/button";
import { Input } from "../components/ui/input";
import { Badge } from "../components/ui/badge";
import { Separator } from "../components/ui/separator";
import { toast } from "../components/ui/sonner";
import { Label } from "../components/ui/label";
import { Select } from "../components/ui/select";

export default function ContactsPage() {
  const { contacts, setContacts, tags, setTags, loading, setLoading } = useCRMStore();
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [selectedTags, setSelectedTags] = useState<number[]>([]);
  const [filterTag, setFilterTag] = useState<number | null>(null);

  useEffect(() => {
    setLoading(true);
    fetchContacts()
      .then(setContacts)
      .catch(() => toast.error("Failed to load contacts"))
      .finally(() => setLoading(false));
    fetchTags()
      .then(setTags)
      .catch(() => toast.error("Failed to load tags"));
  }, [setContacts, setTags, setLoading]);

  const handleAddContact = async () => {
    if (!name || !email) {
      toast.error("Name and email are required");
      return;
    }
    setLoading(true);
    try {
      const contact = await createContact({ name, email, tag_ids: selectedTags });
      setContacts([...contacts, contact]);
      setName("");
      setEmail("");
      setSelectedTags([]);
      toast.success("Contact added");
    } catch {
      toast.error("Failed to add contact");
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteContact = async (id: number) => {
    setLoading(true);
    try {
      await deleteContact(id);
      setContacts(contacts.filter((c) => c.id !== id));
      toast.success("Contact deleted");
    } catch {
      toast.error("Failed to delete contact");
    } finally {
      setLoading(false);
    }
  };

  const filteredContacts = filterTag
    ? contacts.filter((c) => c.tags.some((t) => t.id === filterTag))
    : contacts;

  return (
    <div className="max-w-3xl mx-auto py-8">
      <h2 className="text-2xl font-bold mb-4">Contacts</h2>
      <Card className="mb-6 p-6">
        <h3 className="font-semibold mb-2">Add Contact</h3>
        <div className="flex flex-col gap-2">
          <Label>Name</Label>
          <Input value={name} onChange={(e) => setName(e.target.value)} placeholder="Full Name" />
          <Label>Email</Label>
          <Input value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" />
          <Label>Tags</Label>
          <div className="flex gap-2 flex-wrap">
            {tags.map((tag) => (
              <Badge
                key={tag.id}
                variant={selectedTags.includes(tag.id) ? "default" : "secondary"}
                onClick={() =>
                  setSelectedTags((prev) =>
                    prev.includes(tag.id)
                      ? prev.filter((id) => id !== tag.id)
                      : [...prev, tag.id]
                  )
                }
                className="cursor-pointer"
              >
                {tag.name}
              </Badge>
            ))}
          </div>
          <Button onClick={handleAddContact} disabled={loading} className="mt-2 w-fit">
            Add Contact
          </Button>
        </div>
      </Card>
      <Separator />
      <div className="flex items-center gap-2 my-4">
        <Label>Filter by Tag:</Label>
        <Select
          value={filterTag?.toString() || ""}
          onValueChange={(val) => setFilterTag(val ? parseInt(val) : null)}
        >
          <option value="">All</option>
          {tags.map((tag) => (
            <option key={tag.id} value={tag.id}>
              {tag.name}
            </option>
          ))}
        </Select>
      </div>
      <div className="grid gap-4">
        {filteredContacts.map((contact) => (
          <Card key={contact.id} className="p-4 flex flex-col gap-2">
            <div className="flex justify-between items-center">
              <div>
                <span className="font-semibold">{contact.name}</span>
                <span className="ml-2 text-sm text-muted-foreground">{contact.email}</span>
              </div>
              <Button variant="destructive" size="sm" onClick={() => handleDeleteContact(contact.id)}>
                Delete
              </Button>
            </div>
            <div className="flex gap-2 flex-wrap">
              {contact.tags.map((tag) => (
                <Badge key={tag.id}>{tag.name}</Badge>
              ))}
            </div>
          </Card>
        ))}
        {filteredContacts.length === 0 && (
          <div className="text-muted-foreground text-center py-8">No contacts found.</div>
        )}
      </div>
    </div>
  );
}
</action>

The ContactsPage code is well-structured, but there is a likely issue with the usage of the shadcn/ui `Select` component. In shadcn/ui, `Select` does not accept native `<option>` children; it uses `SelectTrigger`, `SelectContent`, and `SelectItem` for options.

**To fix this:**
- Replace the native `<option>` elements with shadcn/ui `SelectItem` components.
- Ensure the import and usage match shadcn/ui standards.

I'll update ContactsPage.tsx to use the correct shadcn/ui Select pattern.

