import { Contact, Tag } from "../types/crm";

const API_URL = import.meta.env.VITE_API_URL || "http://localhost:8004";

export async function signup(username: string, email: string, password: string) {
  const res = await fetch(`${API_URL}/signup/`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ username, email, password }),
  });
  if (!res.ok) throw new Error(await res.text());
  return await res.json();
}

export async function login(email: string, password: string) {
  const res = await fetch(`${API_URL}/login/`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ email, password }),
  });
  if (!res.ok) throw new Error(await res.text());
  return await res.json();
}

export async function getContacts(token: string): Promise<Contact[]> {
  const res = await fetch(`${API_URL}/contacts/`, {
    headers: { Authorization: `Bearer ${token}` },
  });
  if (!res.ok) throw new Error(await res.text());
  return await res.json();
}

export async function addContact(contact: Partial<Contact>, token: string): Promise<Contact> {
  const res = await fetch(`${API_URL}/contacts/`, {
    method: "POST",
    headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
    body: JSON.stringify(contact),
  });
  if (!res.ok) throw new Error(await res.text());
  return await res.json();
}

export async function getTags(token: string): Promise<Tag[]> {
  const res = await fetch(`${API_URL}/tags/`, {
    headers: { Authorization: `Bearer ${token}` },
  });
  if (!res.ok) throw new Error(await res.text());
  return await res.json();
}

export async function addTag(name: string, token: string): Promise<Tag> {
  const res = await fetch(`${API_URL}/tags/`, {
    method: "POST",
    headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` },
    body: JSON.stringify({ name }),
  });
  if (!res.ok) throw new Error(await res.text());
  return await res.json();
}
