<action type="file" filePath="frontend/src/pages/ContactsPage.tsx">
import React, { useEffect, useState } from "react";
import { useCRMStore } from "../stores/crm-store";
import { getContacts, addContact, getTags, addTag } from "../api/crm";
import { Contact, Tag } from "../types/crm";
import { Card } from "../components/ui/card";
import { Badge } from "../components/ui/badge";
import { Button } from "../components/ui/button";
import { Input } from "../components/ui/input";
import { Dialog, DialogContent, DialogTrigger } from "../components/ui/dialog";
import { toast } from "sonner";
import { useAuthStore } from "../stores/auth-store";

const ContactsPage: React.FC = () => {
  const { contacts, tags, setContacts, setTags, loading, setLoading, error, setError } = useCRMStore();
  const { token } = useAuthStore();
  const [search, setSearch] = useState("");
  const [showDialog, setShowDialog] = useState(false);
  const [newContact, setNewContact] = useState({ name: "", email: "", phone: "", tags: [] as number[] });

  useEffect(() => {
    if (!token) return;
    setLoading(true);
    Promise.all([getContacts(token), getTags(token)])
      .then(([contactsData, tagsData]) => {
        setContacts(contactsData);
        setTags(tagsData);
        setError(null);
      })
      .catch((err) => setError(err.message))
      .finally(() => setLoading(false));
  }, [token, setContacts, setTags, setLoading, setError]);

  const filteredContacts = contacts.filter((c) =>
    c.name.toLowerCase().includes(search.toLowerCase()) ||
    c.email.toLowerCase().includes(search.toLowerCase())
  );

  const handleAddContact = async () => {
    if (!token) return;
    setLoading(true);
    try {
      const contact = await addContact(newContact, token);
      setContacts([...contacts, contact]);
      toast.success("Contact added!");
      setShowDialog(false);
      setNewContact({ name: "", email: "", phone: "", tags: [] });
    } catch (err: any) {
      toast.error(err.message || "Failed to add contact");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6 max-w-5xl mx-auto">
      <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4 mb-6">
        <h1 className="text-3xl font-bold text-primary">Contacts</h1>
        <div className="flex gap-2">
          <Input
            placeholder="Search contacts..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="w-48"
          />
          <Dialog open={showDialog} onOpenChange={setShowDialog}>
            <DialogTrigger asChild>
              <Button variant="primary">Add Contact</Button>
            </DialogTrigger>
            <DialogContent>
              <h2 className="text-xl font-semibold mb-2">Add Contact</h2>
              <Input
                placeholder="Name"
                value={newContact.name}
                onChange={(e) => setNewContact({ ...newContact, name: e.target.value })}
                className="mb-2"
              />
              <Input
                placeholder="Email"
                value={newContact.email}
                onChange={(e) => setNewContact({ ...newContact, email: e.target.value })}
                className="mb-2"
              />
              <Input
                placeholder="Phone"
                value={newContact.phone}
                onChange={(e) => setNewContact({ ...newContact, phone: e.target.value })}
                className="mb-2"
              />
              <div className="mb-2">
                <span className="font-medium">Tags:</span>
                <div className="flex flex-wrap gap-2 mt-1">
                  {tags.map((tag) => (
                    <label key={tag.id} className="flex items-center gap-1">
                      <input
                        type="checkbox"
                        checked={newContact.tags.includes(tag.id)}
                        onChange={(e) => {
                          const checked = e.target.checked;
                          setNewContact((prev) => ({
                            ...prev,
                            tags: checked
                              ? [...prev.tags, tag.id]
                              : prev.tags.filter((id) => id !== tag.id),
                          }));
                        }}
                      />
                      <Badge variant="accent">{tag.name}</Badge>
                    </label>
                  ))}
                </div>
              </div>
              <Button onClick={handleAddContact} className="mt-2 w-full">
                Save Contact
              </Button>
            </DialogContent>
          </Dialog>
        </div>
      </div>
      {loading && <div className="text-muted">Loading...</div>}
      {error && <div className="text-destructive">{error}</div>}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredContacts.map((contact) => (
          <Card key={contact.id} className="p-4 flex flex-col gap-2 shadow-md rounded-lg">
            <div className="flex items-center justify-between">
              <span className="text-lg font-semibold">{contact.name}</span>
              <span className="text-xs text-muted">{contact.email}</span>
            </div>
            <div className="text-sm text-muted">{contact.phone}</div>
            <div className="flex flex-wrap gap-2 mt-2">
              {contact.tags.map((tag) => (
                <Badge key={tag.id} variant="accent">
                  {tag.name}
                </Badge>
              ))}
            </div>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default ContactsPage;
</action>

