from fastapi import APIRouter, Depends, HTTPException, status
from sqlalchemy.orm import Session
from db_config import get_db
from database.tag import Tag
from database.contact import Contact
from models.tag_models import TagCreate, TagRead, TagUpdate
from typing import List

router = APIRouter(prefix="/tags", tags=["tags"])

@router.post("/", response_model=TagRead)
def create_tag(tag: TagCreate, db: Session = Depends(get_db)):
    print("Creating tag:", tag.name)
    db_tag = db.query(Tag).filter(Tag.name == tag.name).first()
    if db_tag:
        raise HTTPException(status_code=400, detail="Tag already exists")
    new_tag = Tag(name=tag.name, description=tag.description)
    db.add(new_tag)
    db.commit()
    db.refresh(new_tag)
    return TagRead.model_validate(new_tag)

@router.get("/", response_model=List[TagRead])
def list_tags(db: Session = Depends(get_db)):
    print("Listing tags")
    tags = db.query(Tag).all()
    return [TagRead.model_validate(t) for t in tags]

@router.put("/{tag_id}/", response_model=TagRead)
def update_tag(tag_id: int, update: TagUpdate, db: Session = Depends(get_db)):
    print("Updating tag:", tag_id)
    tag = db.query(Tag).filter(Tag.id == tag_id).first()
    if not tag:
        raise HTTPException(status_code=404, detail="Tag not found")
    if update.name is not None:
        tag.name = update.name
    if update.description is not None:
        tag.description = update.description
    db.commit()
    db.refresh(tag)
    return TagRead.model_validate(tag)

@router.delete("/{tag_id}/", status_code=status.HTTP_204_NO_CONTENT)
def delete_tag(tag_id: int, db: Session = Depends(get_db)):
    print("Deleting tag:", tag_id)
    tag = db.query(Tag).filter(Tag.id == tag_id).first()
    if not tag:
        raise HTTPException(status_code=404, detail="Tag not found")
    db.delete(tag)
    db.commit()
    return

@router.post("/{tag_id}/assign/", response_model=TagRead)
def assign_tag_to_contact(tag_id: int, contact_id: int, db: Session = Depends(get_db)):
    print(f"Assigning tag {tag_id} to contact {contact_id}")
    tag = db.query(Tag).filter(Tag.id == tag_id).first()
    contact = db.query(Contact).filter(Contact.id == contact_id).first()
    if not tag or not contact:
        raise HTTPException(status_code=404, detail="Tag or Contact not found")
    if contact not in tag.contacts:
        tag.contacts.append(contact)
        db.commit()
    db.refresh(tag)
    return TagRead.model_validate(tag)
</action>

