import { create } from 'zustand'
import { persist } from 'zustand/middleware'
import { Contact } from '../types/contact'
import { fetchContacts, createContact, deleteContact, importContacts } from '../api/contactApi'

interface ContactStore {
  contacts: Contact[]
  loading: boolean
  error: string | null
  fetchContacts: () => Promise<void>
  addContact: (contact: Partial<Contact>) => Promise<void>
  deleteContact: (id: number) => Promise<void>
  importContacts: (file: File) => Promise<void>
}

export const useContactStore = create<ContactStore>()(
  persist(
    (set) => ({
      contacts: [],
      loading: false,
      error: null,
      fetchContacts: async () => {
        set({ loading: true, error: null })
        try {
          const data = await fetchContacts()
          set({ contacts: data, loading: false })
        } catch (e: any) {
          set({ error: e.message, loading: false })
        }
      },
      addContact: async (contact) => {
        set({ loading: true, error: null })
        try {
          await createContact(contact)
          await useContactStore.getState().fetchContacts()
        } catch (e: any) {
          set({ error: e.message, loading: false })
        }
      },
      deleteContact: async (id) => {
        set({ loading: true, error: null })
        try {
          await deleteContact(id)
          await useContactStore.getState().fetchContacts()
        } catch (e: any) {
          set({ error: e.message, loading: false })
        }
      },
      importContacts: async (file) => {
        set({ loading: true, error: null })
        try {
          await importContacts(file)
          await useContactStore.getState().fetchContacts()
        } catch (e: any) {
          set({ error: e.message, loading: false })
        }
      },
    }),
    { name: 'contact-store' }
  )
)
