import { Contact } from '../types/contact'

const API_URL = import.meta.env.VITE_API_URL || 'http://localhost:8005'

export async function fetchContacts(): Promise<Contact[]> {
  const res = await fetch(`${API_URL}/contacts/`)
  if (!res.ok) throw new Error('Failed to fetch contacts')
  return await res.json()
}

export async function createContact(contact: Partial<Contact>): Promise<Contact> {
  const res = await fetch(`${API_URL}/contacts/`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(contact),
  })
  if (!res.ok) throw new Error('Failed to create contact')
  return await res.json()
}

export async function deleteContact(id: number): Promise<void> {
  const res = await fetch(`${API_URL}/contacts/${id}/`, { method: 'DELETE' })
  if (!res.ok) throw new Error('Failed to delete contact')
}

export async function importContacts(file: File): Promise<Contact[]> {
  const formData = new FormData()
  formData.append('file', file)
  const res = await fetch(`${API_URL}/contacts/import/`, {
    method: 'POST',
    body: formData,
  })
  if (!res.ok) throw new Error('Failed to import contacts')
  return await res.json()
}
</action>

