import { useRef } from 'react'
import { Modal, ModalOverlay, ModalContent, ModalHeader, ModalBody, ModalFooter, Button, useToast, Input, Spinner } from '@chakra-ui/react'
import { useContactStore } from '../stores/contactStore'

interface ContactImportModalProps {
  isOpen: boolean
  onClose: () => void
}

const ContactImportModal = ({ isOpen, onClose }: ContactImportModalProps) => {
  const fileRef = useRef<HTMLInputElement>(null)
  const { importContacts, loading, error } = useContactStore()
  const toast = useToast()

  const handleImport = async () => {
    if (fileRef.current?.files && fileRef.current.files[0]) {
      try {
        await importContacts(fileRef.current.files[0])
        toast({ title: 'Contacts imported!', status: 'success' })
        onClose()
      } catch (e: any) {
        toast({ title: 'Import failed', description: e.message, status: 'error' })
      }
    }
  }

  return (
    <Modal isOpen={isOpen} onClose={onClose} isCentered>
      <ModalOverlay />
      <ModalContent>
        <ModalHeader>Import Contacts (CSV)</ModalHeader>
        <ModalBody>
          <Input type="file" accept=".csv" ref={fileRef} />
          {loading && <Spinner mt={4} />}
          {error && <p style={{ color: 'red' }}>{error}</p>}
        </ModalBody>
        <ModalFooter>
          <Button onClick={handleImport} colorScheme="brand" mr={3}>
            Import
          </Button>
          <Button onClick={onClose}>Cancel</Button>
        </ModalFooter>
      </ModalContent>
    </Modal>
  )
}

export default ContactImportModal
