import { useState } from 'react'
import { Modal, ModalOverlay, ModalContent, ModalHeader, ModalBody, ModalFooter, Button, Input, Textarea, Stack, useToast } from '@chakra-ui/react'
import { useNewsletterStore } from '../stores/newsletterStore'

interface NewsletterEditorProps {
  isOpen?: boolean
  onClose?: () => void
  initialTitle?: string
  initialContent?: string
  initialTags?: string[]
  newsletterId?: number
}

const NewsletterEditor = ({
  isOpen = false,
  onClose = () => {},
  initialTitle = '',
  initialContent = '',
  initialTags = [],
  newsletterId,
}: NewsletterEditorProps) => {
  const [title, setTitle] = useState(initialTitle)
  const [content, setContent] = useState(initialContent)
  const [tags, setTags] = useState(initialTags.join(', '))
  const { addNewsletter, updateNewsletter } = useNewsletterStore()
  const toast = useToast()

  const handleSave = async () => {
    try {
      const tagList = tags.split(',').map((t) => t.trim()).filter(Boolean)
      if (newsletterId) {
        await updateNewsletter(newsletterId, { title, content, tags: tagList })
        toast({ title: 'Newsletter updated!', status: 'success' })
      } else {
        await addNewsletter({ title, content, tags: tagList })
        toast({ title: 'Newsletter created!', status: 'success' })
      }
      onClose()
    } catch (e: any) {
      toast({ title: 'Error', description: e.message, status: 'error' })
    }
  }

  return (
    <Modal isOpen={isOpen} onClose={onClose} isCentered>
      <ModalOverlay />
      <ModalContent>
        <ModalHeader>{newsletterId ? 'Edit Newsletter' : 'Create Newsletter'}</ModalHeader>
        <ModalBody>
          <Stack spacing={4}>
            <Input
              placeholder="Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
            <Textarea
              placeholder="Write your newsletter content here..."
              value={content}
              onChange={(e) => setContent(e.target.value)}
              rows={10}
            />
            <Input
              placeholder="Tags (comma separated)"
              value={tags}
              onChange={(e) => setTags(e.target.value)}
            />
          </Stack>
        </ModalBody>
        <ModalFooter>
