import { useState } from 'react'
import { Box, Heading, Input, Button, Stack, useToast } from '@chakra-ui/react'

const API_URL = import.meta.env.VITE_API_URL || 'http://localhost:8005'

const SubscriberForm = () => {
  const [email, setEmail] = useState('')
  const [name, setName] = useState('')
  const [loading, setLoading] = useState(false)
  const toast = useToast()

  const handleSubscribe = async () => {
    setLoading(true)
    try {
      const res = await fetch(`${API_URL}/subscribe/`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, name }),
      })
      if (!res.ok) throw new Error('Subscription failed')
      toast({ title: 'Subscribed!', status: 'success' })
      setEmail('')
      setName('')
    } catch (e: any) {
      toast({ title: 'Error', description: e.message, status: 'error' })
    }
    setLoading(false)
  }

  return (
    <Box maxW="md" mx="auto" mt={12} p={8} bg="white" borderRadius="lg" boxShadow="md">
      <Heading size="md" mb={6}>Subscribe to Our Newsletter</Heading>
      <Stack spacing={4}>
        <Input
          placeholder="Your name (optional)"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <Input
          placeholder="Your email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          type="email"
        />
        <Button
          colorScheme="brand"
          onClick={handleSubscribe}
          isLoading={loading}
          isDisabled={!email}
        >
          Subscribe
        </Button>
      </Stack>
    </Box>
  )
}

