import axios from "axios";

const BASE_URL = import.meta.env.VITE_BACKEND_URL || "http://localhost:8004";

export const getContacts = async () => {
  const resp = await axios.get(`${BASE_URL}/contacts`);
  return resp.data;
};

export const addContact = async (contact) => {
  const resp = await axios.post(`${BASE_URL}/contacts`, contact);
  return resp.data;
};

export const importContacts = async (file) => {
  const formData = new FormData();
  formData.append("file", file);
  const resp = await axios.post(`${BASE_URL}/contacts/import`, formData, {
    headers: { "Content-Type": "multipart/form-data" },
  });
  return resp.data;
};

export const getTags = async () => {
  const resp = await axios.get(`${BASE_URL}/tags`);
  return resp.data;
};

export const addTag = async (tag) => {
  const resp = await axios.post(`${BASE_URL}/tags`, tag);
  return resp.data;
};
