import React, { useEffect, useState } from "react";
import {
  Box,
  Container,
  Heading,
  VStack,
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td,
  Tag,
  Spinner,
  Alert,
  AlertIcon,
  Button,
  Input,
  HStack,
  Select,
  useToast,
} from "@chakra-ui/react";

const BACKEND_URL = import.meta.env.VITE_BACKEND_URL || "http://localhost:8005";

type Contact = {
  id: number;
  email: string;
  name?: string;
  tags: string[];
  subscribed: boolean;
};

export default function ContactsPage() {
  const [contacts, setContacts] = useState<Contact[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [selectedTag, setSelectedTag] = useState<string>("");
  const [importing, setImporting] = useState(false);
  const [file, setFile] = useState<File | null>(null);
  const toast = useToast();

  // Fetch contacts
  const fetchContacts = async (tag?: string) => {
    setLoading(true);
    setError(null);
    try {
      const url = tag ? `${BACKEND_URL}/contacts?tag=${encodeURIComponent(tag)}` : `${BACKEND_URL}/contacts`;
      const res = await fetch(url);
      if (!res.ok) throw new Error("Failed to fetch contacts");
      const data = await res.json();
      setContacts(data);
    } catch (e: any) {
      setError(e.message);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchContacts(selectedTag);
    // eslint-disable-next-line
  }, [selectedTag]);

  // Collect all tags for filter dropdown
  const allTags = Array.from(
    new Set(contacts.flatMap((c) => c.tags))
  ).filter(Boolean);

  // Handle CSV import
  const handleImport = async () => {
    if (!file) return;
    setImporting(true);
    setError(null);
    try {
      const formData = new FormData();
      formData.append("file", file);
      const res = await fetch(`${BACKEND_URL}/contacts/import`, {
        method: "POST",
        body: formData,
      });
      if (!res.ok) throw new Error("Failed to import contacts");
      toast({
        title: "Contacts imported!",
        status: "success",
        duration: 3000,
        isClosable: true,
      });
      setFile(null);
      fetchContacts(selectedTag);
    } catch (e: any) {
      setError(e.message);
    }
    setImporting(false);
  };

  return (
    <Box minH="100vh" bg="gray.50">
      <Container maxW="5xl" py={8}>
        <VStack spacing={6} align="stretch">
          <Heading size="lg" color="brand.500">
            Contacts Management
          </Heading>
          <HStack spacing={4}>
            <Select
              placeholder="Filter by tag"
              value={selectedTag}
              onChange={(e) => setSelectedTag(e.target.value)}
              minW="180px"
            >
              {allTags.map((tag) => (
                <option key={tag} value={tag}>
                  {tag}
                </option>
              ))}
            </Select>
            <Input
              type="file"
              accept=".csv"
              onChange={(e) => setFile(e.target.files?.[0] || null)}
              w="auto"
              p={1}
              border="1px solid"
              borderColor="gray.300"
              bg="white"
            />
            <Button
              colorScheme="brand"
              onClick={handleImport}
              isLoading={importing}
              disabled={!file}
            >
              Import CSV
            </Button>
            <Button
              variant="outline"
              onClick={() => {
                setSelectedTag("");
                fetchContacts();
              }}
            >
              Clear Filter
            </Button>
          </HStack>
          {loading ? (
            <Spinner size="lg" />
          ) : error ? (
            <Alert status="error">
              <AlertIcon />
              {error}
            </Alert>
          ) : (
            <Table variant="simple" size="md" bg="white" borderRadius="md" boxShadow="md">
              <Thead>
                <Tr>
                  <Th>Email</Th>
                  <Th>Name</Th>
                  <Th>Tags</Th>
                  <Th>Subscribed</Th>
                </Tr>
              </Thead>
              <Tbody>
                {contacts.map((contact) => (
                  <Tr key={contact.id}>
                    <Td>{contact.email}</Td>
                    <Td>{contact.name || "-"}</Td>
                    <Td>
                      <HStack spacing={1}>
                        {contact.tags.map((tag) => (
                          <Tag key={tag} colorScheme="blue" size="sm">
                            {tag}
                          </Tag>
                        ))}
                      </HStack>
                    </Td>
                    <Td>
                      {contact.subscribed ? (
                        <Tag colorScheme="green" size="sm">
                          Yes
                        </Tag>
                      ) : (
                        <Tag colorScheme="red" size="sm">
                          No
                        </Tag>
                      )}
                    </Td>
                  </Tr>
                ))}
              </Tbody>
            </Table>
          )}
        </VStack>
      </Container>
    </Box>
  );
}
</action>

