  Text,
  Spinner,
  Alert,
  AlertIcon,
} from "@chakra-ui/react";

const BACKEND_URL = import.meta.env.VITE_BACKEND_URL || "http://localhost:8005";

export default function SubscribePage() {
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [tags, setTags] = useState<string[]>([]);
  const [tagInput, setTagInput] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState(false);
  const toast = useToast();

  const handleAddTag = () => {
    if (tagInput && !tags.includes(tagInput)) {
      setTags([...tags, tagInput]);
      setTagInput("");
    }
  };

  const handleRemoveTag = (tag: string) => {
    setTags(tags.filter((t) => t !== tag));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(false);
    try {
      const res = await fetch(`${BACKEND_URL}/contacts/subscribe`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, name, tags }),
      });
      if (!res.ok) throw new Error("Failed to subscribe");
      setSuccess(true);
      toast({
        title: "Subscribed!",
        description: "You have been added to the email list.",
        status: "success",
        duration: 3000,
        isClosable: true,
      });
      setEmail("");
      setName("");
      setTags([]);
    } catch (e: any) {
      setError(e.message);
    }
    setLoading(false);
  };

  return (
    <Box minH="100vh" bg="gray.50">
      <Container maxW="md" py={12}>
        <VStack spacing={6} align="stretch">
          <Heading size="lg" color="brand.500" textAlign="center">
