          </Heading>
          <form onSubmit={handleSubmit}>
            <VStack spacing={4}>
              <FormControl isRequired isInvalid={!!error && !email}>
                <FormLabel>Email address</FormLabel>
                <Input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="your@email.com"
                  size="lg"
                />
                <FormErrorMessage>Email is required</FormErrorMessage>
              </FormControl>
              <FormControl>
                <FormLabel>Name (optional)</FormLabel>
                <Input
                  type="text"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  placeholder="Your name"
                  size="lg"
                />
              </FormControl>
              <FormControl>
                <FormLabel>Tags (optional)</FormLabel>
                <HStack>
                  <Input
                    type="text"
                    value={tagInput}
                    onChange={(e) => setTagInput(e.target.value)}
                    placeholder="Add tag"
                    size="md"
                  />
                  <Button onClick={handleAddTag} size="md" colorScheme="blue">
                    Add
                  </Button>
                </HStack>
                <HStack spacing={2} mt={2}>
                  {tags.map((tag) => (
                    <Tag key={tag} colorScheme="blue" size="md" cursor="pointer" onClick={() => handleRemoveTag(tag)}>
                      {tag} <Text ml={1} as="span" fontWeight="bold">Ã—</Text>
                    </Tag>
                  ))}
                </HStack>
              </FormControl>
              <Button
                colorScheme="brand"
                type="submit"
                size="lg"
                isLoading={loading}
                isDisabled={!email}
                w="full"
              >
                Subscribe
              </Button>
            </VStack>
          </form>
          {loading && <Spinner size="lg" />}
          {error && (
            <Alert status="error">
              <AlertIcon />
              {error}
            </Alert>
          )}
          {success && (
            <Alert status="success">
              <AlertIcon />
              Subscribed successfully!
            </Alert>
          )}
        </VStack>
      </Container>
    </Box>
  );
}
