from sqlalchemy.orm import Session
from sqlalchemy import or_
from typing import List, Optional
from database.contact import Contact, Tag, Newsletter
from schemas.contact import ContactCreate, ContactUpdate, TagCreate, NewsletterCreate, NewsletterUpdate
from datetime import datetime

def get_contact(db: Session, contact_id: int) -> Optional[Contact]:
    return db.query(Contact).filter(Contact.id == contact_id).first()

def get_contact_by_email(db: Session, email: str) -> Optional[Contact]:
    return db.query(Contact).filter(Contact.email == email).first()

def get_contacts(db: Session, skip: int = 0, limit: int = 100) -> List[Contact]:
    return db.query(Contact).offset(skip).limit(limit).all()

def get_contacts_by_tag(db: Session, tag_id: int) -> List[Contact]:
    return db.query(Contact).join(Contact.tags).filter(Tag.id == tag_id).all()

def create_contact(db: Session, contact: ContactCreate) -> Contact:
    db_contact = Contact(
        email=contact.email,
        first_name=contact.first_name,
        last_name=contact.last_name,
        is_subscribed=contact.is_subscribed
    )
    db.add(db_contact)
    db.commit()
    db.refresh(db_contact)
    return db_contact

def update_contact(db: Session, contact_id: int, contact_update: ContactUpdate) -> Optional[Contact]:
    db_contact = get_contact(db, contact_id)
    if not db_contact:
        return None

    if contact_update.email is not None:
        db_contact.email = contact_update.email
    if contact_update.first_name is not None:
        db_contact.first_name = contact_update.first_name
    if contact_update.last_name is not None:
        db_contact.last_name = contact_update.last_name
    if contact_update.is_subscribed is not None:
        db_contact.is_subscribed = contact_update.is_subscribed
    if contact_update.tag_ids is not None:
        # Update tags
        tags = db.query(Tag).filter(Tag.id.in_(contact_update.tag_ids)).all()
        db_contact.tags = tags

    db.commit()
    db.refresh(db_contact)
    return db_contact

def delete_contact(db: Session, contact_id: int) -> bool:
    db_contact = get_contact(db, contact_id)
    if not db_contact:
        return False
    db.delete(db_contact)
    db.commit()
    return True

def search_contacts(db: Session, query: str) -> List[Contact]:
    return db.query(Contact).filter(
        or_(
            Contact.email.contains(query),
            Contact.first_name.contains(query),
            Contact.last_name.contains(query)
        )
    ).all()

def get_tags(db: Session) -> List[Tag]:
    return db.query(Tag).all()

def get_tag(db: Session, tag_id: int) -> Optional[Tag]:
    return db.query(Tag).filter(Tag.id == tag_id).first()

def create_tag(db: Session, tag: TagCreate) -> Tag:
    db_tag = Tag(name=tag.name)
    db.add(db_tag)
    db.commit()
    db.refresh(db_tag)
    return db_tag

def delete_tag(db: Session, tag_id: int) -> bool:
    db_tag = get_tag(db, tag_id)
    if not db_tag:
        return False
    db.delete(db_tag)
    db.commit()
    return True

def add_contact_to_tag(db: Session, contact_id: int, tag_id: int) -> bool:
    db_contact = get_contact(db, contact_id)
    db_tag = get_tag(db, tag_id)

    if not db_contact or not db_tag:
        return False

    if db_tag not in db_contact.tags:
        db_contact.tags.append(db_tag)
        db.commit()
    return True

def remove_contact_from_tag(db: Session, contact_id: int, tag_id: int) -> bool:
    db_contact = get_contact(db, contact_id)
    db_tag = get_tag(db, tag_id)

    if not db_contact or not db_tag:
        return False

    if db_tag in db_contact.tags:
        db_contact.tags.remove(db_tag)
        db.commit()
    return True

def get_newsletters(db: Session, skip: int = 0, limit: int = 100) -> List[Newsletter]:
    return db.query(Newsletter).offset(skip).limit(limit).all()

def get_newsletter(db: Session, newsletter_id: int) -> Optional[Newsletter]:
    return db.query(Newsletter).filter(Newsletter.id == newsletter_id).first()

def create_newsletter(db: Session, newsletter: NewsletterCreate) -> Newsletter:
    db_newsletter = Newsletter(
        subject=newsletter.subject,
        content=newsletter.content
    )
    db.add(db_newsletter)
    db.commit()
    db.refresh(db_newsletter)
    return db_newsletter

def update_newsletter(db: Session, newsletter_id: int, newsletter_update: NewsletterUpdate) -> Optional[Newsletter]:
    db_newsletter = get_newsletter(db, newsletter_id)
    if not db_newsletter:
        return None

    if newsletter_update.subject is not None:
        db_newsletter.subject = newsletter_update.subject
    if newsletter_update.content is not None:
        db_newsletter.content = newsletter_update.content
    if newsletter_update.sent_at is not None:
        db_newsletter.sent_at = newsletter_update.sent_at

    db.commit()
    db.refresh(db_newsletter)
    return db_newsletter

def delete_newsletter(db: Session, newsletter_id: int) -> bool:
    db_newsletter = get_newsletter(db, newsletter_id)
    if not db_newsletter:
        return False
    db.delete(db_newsletter)
    db.commit()
    return True

def send_newsletter(db: Session, newsletter_id: int, tag_ids: List[int]) -> int:
    """
    Send newsletter to contacts with specified tags
    Returns number of contacts the newsletter was sent to
    """
    # Mark newsletter as sent
    newsletter = get_newsletter(db, newsletter_id)
    if not newsletter:
        return 0

    newsletter.sent_at = datetime.now()
    db.commit()

    # Get contacts with specified tags
    contacts = db.query(Contact).join(Contact.tags).filter(Tag.id.in_(tag_ids)).distinct(Contact.id).all()

    # In a real implementation, we would send emails here
    # For now, we just return the count
    return len(contacts)
