from fastapi import APIRouter, Depends, HTTPException, status
from sqlalchemy.orm import Session
from typing import List
import json

from db_config import get_db
from schemas.contact import Contact, ContactCreate, ContactUpdate, Tag, TagCreate, Newsletter, NewsletterCreate, NewsletterUpdate, ContactImport, NewsletterSend
from services.contact_service import *

router = APIRouter(prefix="/api/contacts", tags=["contacts"])

@router.get("/", response_model=List[Contact])
def read_contacts(skip: int = 0, limit: int = 100, db: Session = Depends(get_db)):
    contacts = get_contacts(db, skip=skip, limit=limit)
    return contacts

@router.get("/{contact_id}", response_model=Contact)
def read_contact(contact_id: int, db: Session = Depends(get_db)):
    db_contact = get_contact(db, contact_id=contact_id)
    if db_contact is None:
        raise HTTPException(status_code=404, detail="Contact not found")
    return db_contact

@router.post("/", response_model=Contact)
def create_contact(contact: ContactCreate, db: Session = Depends(get_db)):
    db_contact = get_contact_by_email(db, email=contact.email)
    if db_contact:
        raise HTTPException(status_code=400, detail="Contact with this email already exists")
    return create_contact(db=db, contact=contact)

@router.put("/{contact_id}", response_model=Contact)
def update_contact(contact_id: int, contact_update: ContactUpdate, db: Session = Depends(get_db)):
    db_contact = update_contact(db=db, contact_id=contact_id, contact_update=contact_update)
    if db_contact is None:
        raise HTTPException(status_code=404, detail="Contact not found")
    return db_contact

@router.delete("/{contact_id}")
def delete_contact(contact_id: int, db: Session = Depends(get_db)):
    success = delete_contact(db=db, contact_id=contact_id)
    if not success:
        raise HTTPException(status_code=404, detail="Contact not found")
    return {"message": "Contact deleted successfully"}

@router.post("/import", response_model=dict)
def import_contacts(contacts_import: ContactImport, db: Session = Depends(get_db)):
    created_count = 0
    updated_count = 0

    for contact_data in contacts_import.contacts:
        existing_contact = get_contact_by_email(db, email=contact_data.email)
        if existing_contact:
            # Update existing contact
            update_contact(db, existing_contact.id, ContactUpdate(
                first_name=contact_data.first_name,
                last_name=contact_data.last_name,
                is_subscribed=contact_data.is_subscribed
            ))
            updated_count += 1
        else:
            # Create new contact
            create_contact(db, contact_data)
            created_count += 1

    return {
        "message": f"Import completed: {created_count} contacts created, {updated_count} contacts updated",
        "created": created_count,
        "updated": updated_count
    }

@router.get("/search/{query}", response_model=List[Contact])
def search_contacts(query: str, db: Session = Depends(get_db)):
    contacts = search_contacts(db, query=query)
    return contacts

# Tag routes
@router.get("/tags/", response_model=List[Tag])
def read_tags(db: Session = Depends(get_db)):
    tags = get_tags(db)
    return tags

@router.post("/tags/", response_model=Tag)
def create_tag(tag: TagCreate, db: Session = Depends(get_db)):
    # Check if tag already exists
    existing_tag = db.query(Tag).filter(Tag.name == tag.name).first()
    if existing_tag:
        raise HTTPException(status_code=400, detail="Tag already exists")
    return create_tag(db=db, tag=tag)

@router.delete("/tags/{tag_id}")
def delete_tag(tag_id: int, db: Session = Depends(get_db)):
    success = delete_tag(db=db, tag_id=tag_id)
    if not success:
        raise HTTPException(status_code=404, detail="Tag not found")
    return {"message": "Tag deleted successfully"}

@router.post("/{contact_id}/tags/{tag_id}")
def add_contact_to_tag(contact_id: int, tag_id: int, db: Session = Depends(get_db)):
    success = add_contact_to_tag(db=db, contact_id=contact_id, tag_id=tag_id)
    if not success:
        raise HTTPException(status_code=404, detail="Contact or tag not found")
    return {"message": "Tag added to contact successfully"}

@router.delete("/{contact_id}/tags/{tag_id}")
def remove_contact_from_tag(contact_id: int, tag_id: int, db: Session = Depends(get_db)):
    success = remove_contact_from_tag(db=db, contact_id=contact_id, tag_id=tag_id)
    if not success:
        raise HTTPException(status_code=404, detail="Contact or tag not found")
    return {"message": "Tag removed from contact successfully"}

# Newsletter routes
newsletter_router = APIRouter(prefix="/api/newsletters", tags=["newsletters"])

@newsletter_router.get("/", response_model=List[Newsletter])
def read_newsletters(skip: int = 0, limit: int = 100, db: Session = Depends(get_db)):
    newsletters = get_newsletters(db, skip=skip, limit=limit)
    return newsletters

@newsletter_router.get("/{newsletter_id}", response_model=Newsletter)
def read_newsletter(newsletter_id: int, db: Session = Depends(get_db)):
    db_newsletter = get_newsletter(db, newsletter_id=newsletter_id)
    if db_newsletter is None:
        raise HTTPException(status_code=404, detail="Newsletter not found")
    return db_newsletter

@newsletter_router.post("/", response_model=Newsletter)
def create_newsletter(newsletter: NewsletterCreate, db: Session = Depends(get_db)):
    return create_newsletter(db=db, newsletter=newsletter)

@newsletter_router.put("/{newsletter_id}", response_model=Newsletter)
def update_newsletter(newsletter_id: int, newsletter_update: NewsletterUpdate, db: Session = Depends(get_db)):
    db_newsletter = update_newsletter(db=db, newsletter_id=newsletter_id, newsletter_update=newsletter_update)
    if db_newsletter is None:
        raise HTTPException(status_code=404, detail="Newsletter not found")
    return db_newsletter

@newsletter_router.delete("/{newsletter_id}")
def delete_newsletter(newsletter_id: int, db: Session = Depends(get_db)):
    success = delete_newsletter(db=db, newsletter_id=newsletter_id)
    if not success:
        raise HTTPException(status_code=404, detail="Newsletter not found")
    return {"message": "Newsletter deleted successfully"}

@newsletter_router.post("/send", response_model=dict)
def send_newsletter(newsletter_send: NewsletterSend, db: Session = Depends(get_db)):
    count = send_newsletter(db=db, newsletter_id=newsletter_send.newsletter_id, tag_ids=newsletter_send.tag_ids)
    return {
        "message": f"Newsletter sent to {count} contacts",
        "sent_count": count
    }

# Subscription route (public endpoint for newsletter signup)
subscription_router = APIRouter(prefix="/api/subscribe", tags=["subscription"])

@subscription_router.post("/", response_model=Contact)
def subscribe_contact(contact: ContactCreate, db: Session = Depends(get_db)):
    # Check if contact already exists
    db_contact = get_contact_by_email(db, email=contact.email)
    if db_contact:
        # Update existing contact to subscribed
        db_contact.is_subscribed = True
        db.commit()
        db.refresh(db_contact)
        return db_contact
    else:
        # Create new contact
        return create_contact(db=db, contact=contact)
