// --- Interfaces for Data Models ---
interface BlogPost {
  id: number;
  title: string;
}

interface Newsletter {
  id: number;
  subject: string;
  body: string;
  blog_post_id?: number | null;
  blog_post?: BlogPost; // Nested blog post object
