        });
        fetchTags(); // Refresh tags list
        fetchContacts(); // Refresh contacts to reflect tag removal
      } catch (err: AxiosError) { // Changed err: any to err: AxiosError
        toast({
          title: 'Error',
          description: (err.response?.data as any)?.detail || 'Failed to delete tag.',
          status: 'error',
          duration: 5000,
          isClosable: true,
        });
      }
    }
  };

  const handleTagFormChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setTagFormData({ name: e.target.value });
  };

  const handleTagFormSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      if (currentTag) {
        // Update existing tag
        await api.put(`/tags/${currentTag.id}`, tagFormData);
        toast({
          title: 'Tag updated.',
          status: 'success',
          duration: 3000,
          isClosable: true,
        });
