const ContactsPage: React.FC = () => {
  const [contacts, setContacts] = useState<Contact[]>([]);
  const [tags, setTags] = useState<Tag[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false); // For Contact Add/Edit
  const [isTagModalOpen, setIsTagModalOpen] = useState(false); // For Tag Management
  const [currentContact, setCurrentContact] = useState<Contact | null>(null);
  const [currentTag, setCurrentTag] = useState<Tag | null>(null); // For Tag Add/Edit
  const [tagFormData, setTagFormData] = useState({ name: '' }); // For Tag Add/Edit
  const [formData, setFormData] = useState<ContactFormData>({
    email: '',
    first_name: '',
    last_name: '',
    subscribed: true,
    tag_ids: [],
  });
  const [searchTerm, setSearchTerm] = useState('');
  const toast = useToast();

  const fetchContacts = useCallback(async () => {
    try {
      setLoading(true);
      const response = await api.get<Contact[]>('/contacts/');
      setContacts(response.data);
    } catch (err: AxiosError) {
      if (err.isAxiosError && err.response) {
        const errorDetail = (err.response.data as ApiErrorResponse)?.detail || 'Failed to fetch contacts.';
        setError(errorDetail);
        toast({
          title: 'Error',
          description: errorDetail,
          status: 'error',
          duration: 5000,
          isClosable: true,
        });
      } else {
        setError('An unexpected error occurred.');
        toast({
          title: 'Error',
          description: 'An unexpected error occurred.',
          status: 'error',
          duration: 5000,
          isClosable: true,
        });
      }
    } finally {
      setLoading(false);
    }
  }, [toast]);

  const fetchTags = useCallback(async () => {
    try {
      const response = await api.get<Tag[]>('/tags/');
      setTags(response.data);
    } catch (err: AxiosError) {
      if (err.isAxiosError && err.response) {
        const errorDetail = (err.response.data as ApiErrorResponse)?.detail || 'Failed to fetch tags.';
        toast({
          title: 'Error',
          description: errorDetail,
          status: 'error',
          duration: 5000,
          isClosable: true,
        });
      } else {
        toast({
          title: 'Error',
          description: 'An unexpected error occurred.',
          status: 'error',
          duration: 5000,
          isClosable: true,
        });
      }
    }
  }, [toast]);

  useEffect(() => {
    fetchContacts();
    fetchTags();
  }, [fetchContacts, fetchTags]);

  const handleAddContact = () => {
    setCurrentContact(null);
    setFormData({
      email: '',
      first_name: '',
      last_name: '',
      subscribed: true,
      tag_ids: [],
    });
    setIsModalOpen(true);
  };

  const handleEditContact = (contact: Contact) => {
    setCurrentContact(contact);
    setFormData({
      email: contact.email,
      first_name: contact.first_name || '',
      last_name: contact.last_name || '',
      subscribed: contact.subscribed === 1,
      tag_ids: contact.tags.map((tag) => tag.id),
    });
    setIsModalOpen(true);
  };

  const handleDeleteContact = async (contactId: number) => {
    if (window.confirm('Are you sure you want to delete this contact?')) {
      try {
        await api.delete(`/contacts/${contactId}`);
        toast({
          title: 'Contact deleted.',
          status: 'success',
          duration: 3000,
          isClosable: true,
        });
        fetchContacts(); // Refresh list
      } catch (err: AxiosError) {
        if (err.isAxiosError && err.response) {
          const errorDetail = (err.response.data as ApiErrorResponse)?.detail || 'Failed to delete contact.';
          toast({
            title: 'Error',
            description: errorDetail,
            status: 'error',
            duration: 5000,
            isClosable: true,
          });
        } else {
          toast({
            title: 'Error',
            description: 'An unexpected error occurred.',
            status: 'error',
            duration: 5000,
            isClosable: true,
          });
        }
      }
    }
  };

  const handleFormChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value, type } = e.target;
    if (type === 'checkbox') {
      setFormData((prev) => ({ ...prev, [name]: (e.target as HTMLInputElement).checked }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleTagChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const selectedOptions = Array.from(e.target.selectedOptions).map((option) =>
      parseInt(option.value, 10)
    );
    setFormData((prev) => ({ ...prev, tag_ids: selectedOptions }));
  };

  const handleFormSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const payload = {
        ...formData,
        subscribed: formData.subscribed ? 1 : 0, // Convert boolean back to 1 or 0
      };

      if (currentContact) {
        // Update existing contact
        await api.put(`/contacts/${currentContact.id}`, payload);
        toast({
          title: 'Contact updated.',
          status: 'success',
          duration: 3000,
          isClosable: true,
        });
      } else {
        // Create new contact
        await api.post('/contacts/', payload);
        toast({
          title: 'Contact created.',
          status: 'success',
          duration: 3000,
          isClosable: true,
        });
      }
      setIsModalOpen(false);
      fetchContacts(); // Refresh list
    } catch (err: AxiosError) {
      if (err.isAxiosError && err.response) {
        const errorDetail = (err.response.data as ApiErrorResponse)?.detail || 'Failed to save contact.';
        toast({
          title: 'Error',
          description: errorDetail,
          status: 'error',
          duration: 5000,
          isClosable: true,
        });
      } else {
        toast({
          title: 'Error',
          description: 'An unexpected error occurred.',
          status: 'error',
          duration: 5000,
          isClosable: true,
        });
      }
    }
  };

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await api.post('/contacts/import-csv/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      toast({
        title: 'CSV Import Complete',
        description: `${response.data.imported_count} new contacts imported, ${response.data.updated_count} contacts updated.`,
        status: 'success',
        duration: 5000,
        isClosable: true,
      });
      fetchContacts(); // Refresh list
    } catch (err: AxiosError) {
      if (err.isAxiosError && err.response) {
        const errorDetail = (err.response.data as ApiErrorResponse)?.detail || 'Failed to import contacts from CSV.';
        toast({
          title: 'Error',
          description: errorDetail,
          status: 'error',
          duration: 5000,
          isClosable: true,
        });
      } else {
        toast({
          title: 'Error',
          description: 'An unexpected error occurred.',
          status: 'error',
          duration: 5000,
          isClosable: true,
        });
      }
    }
  };

  // --- Tag Management Functions ---
  const handleOpenTagModal = () => {
    setCurrentTag(null);
    setTagFormData({ name: '' });
    setIsTagModalOpen(true);
  };

  const handleEditTag = (tag: Tag) => {
    setCurrentTag(tag);
    setTagFormData({ name: tag.name });
    setIsTagModalOpen(true);
  };

  const handleDeleteTag = async (tagId: number) => {
    if (window.confirm('Are you sure you want to delete this tag? This will also remove it from all associated contacts.')) {
      try {
        await api.delete(`/tags/${tagId}`);
        toast({
          title: 'Tag deleted.',
          status: 'success',
          duration: 3000,
          isClosable: true,
        });
        fetchTags(); // Refresh tags list
        fetchContacts(); // Refresh contacts to reflect tag removal
      } catch (err: AxiosError) {
        if (err.isAxiosError && err.response) {
          const errorDetail = (err.response.data as ApiErrorResponse)?.detail || 'Failed to delete tag.';
          toast({
            title: 'Error',
            description: errorDetail,
            status: 'error',
            duration: 5000,
            isClosable: true,
          });
        } else {
          toast({
            title: 'Error',
            description: 'An unexpected error occurred.',
            status: 'error',
            duration: 5000,
            isClosable: true,
          });
        }
      }
    }
  };

  const handleTagFormChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setTagFormData({ name: e.target.value });
  };

  const handleTagFormSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      if (currentTag) {
        // Update existing tag
        await api.put(`/tags/${currentTag.id}`, tagFormData);
        toast({
          title: 'Tag updated.',
          status: 'success',
          duration: 3000,
          isClosable: true,
        });
      } else {
        // Create new tag
        await api.post('/tags/', tagFormData);
        toast({
          title: 'Tag created.',
          status: 'success',
          duration: 3000,
          isClosable: true,
        });
      }
      setIsTagModalOpen(false);
      fetchTags(); // Refresh tags list
    } catch (err: AxiosError) {
      if (err.isAxiosError && err.response) {
        const errorDetail = (err.response.data as ApiErrorResponse)?.detail || 'Failed to save tag.';
        toast({
          title: 'Error',
          description: errorDetail,
          status: 'error',
          duration: 5000,
          isClosable: true,
        });
      } else {
        toast({
          title: 'Error',
          description: 'An unexpected error occurred.',
          status: 'error',
          duration: 5000,
          isClosable: true,
        });
      }
    }
  };


  const filteredContacts = contacts.filter(
    (contact) =>
      contact.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (contact.first_name && contact.first_name.toLowerCase().includes(searchTerm.toLowerCase())) ||
      (contact.last_name && contact.last_name.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  if (loading) {
    return (
      <Box p={5} textAlign="center">
        <Spinner size="xl" />
        <Text mt={4}>Loading contacts...</Text>
      </Box>
    );
  }

  if (error) {
    return (
      <Box p={5}>
        <Alert status="error">
          <AlertIcon />
          {error}
        </Alert>
      </Box>
    );
  }

  return (
    <Box p={5}>
      <Flex mb={4} alignItems="center">
        <Heading size="lg">Contacts Management</Heading>
        <Spacer />
        <InputGroup maxW="300px" mr={4}>
          <InputLeftElement pointerEvents="none">
            <SearchIcon color="gray.300" />
          </InputLeftElement>
          <Input
            type="text"
            placeholder="Search contacts..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </InputGroup>
        <Button leftIcon={<AddIcon />} colorScheme="primary" onClick={handleAddContact} mr={2}>
          Add Contact
        </Button>
        <Button leftIcon={<UpDownIcon />} colorScheme="teal" as="label" htmlFor="csv-upload" mr={2}>
          Import CSV
          <Input type="file" id="csv-upload" accept=".csv" onChange={handleFileUpload} display="none" />
        </Button>
        <Button leftIcon={<EditIcon />} colorScheme="purple" onClick={handleOpenTagModal}>
          Manage Tags
        </Button>
      </Flex>

      {filteredContacts.length === 0 ? (
        <Text>No contacts found. Add a new contact or import from CSV.</Text>
      ) : (
        <Table variant="simple">
          <Thead>
            <Tr>
              <Th>Email</Th>
              <Th>Name</Th>
              <Th>Subscribed</Th>
              <Th>Tags</Th>
              <Th>Actions</Th>
            </Tr>
          </Thead>
          <Tbody>
            {filteredContacts.map((contact) => (
              <Tr key={contact.id}>
                <Td>{contact.email}</Td>
                <Td>{contact.first_name} {contact.last_name}</Td>
                <Td>{contact.subscribed === 1 ? 'Yes' : 'No'}</Td>
                <Td>
                  <HStack spacing={1}>
                    {contact.tags.map((tag) => (
                      <Tag size="sm" key={tag.id} variant="solid" colorScheme="blue">
                        <TagLabel>{tag.name}</TagLabel>
                      </Tag>
                    ))}
                  </HStack>
                </Td>
                <Td>
                  <HStack spacing={2}>
                    <Button size="sm" leftIcon={<EditIcon />} onClick={() => handleEditContact(contact)}>
                      Edit
                    </Button>
                    <Button size="sm" leftIcon={<DeleteIcon />} colorScheme="red" onClick={() => handleDeleteContact(contact.id)}>
                      Delete
                    </Button>
                  </HStack>
                </Td>
              </Tr>
            ))}
          </Tbody>
        </Table>
      )}

      {/* Add/Edit Contact Modal */}
      <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>{currentContact ? 'Edit Contact' : 'Add New Contact'}</ModalHeader>
          <ModalCloseButton />
          <form onSubmit={handleFormSubmit}>
            <ModalBody>
              <VStack spacing={4}>
                <FormControl id="email" isRequired>
                  <FormLabel>Email</FormLabel>
                  <Input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleFormChange}
                    readOnly={!!currentContact} // Email should not be editable for existing contacts
                  />
                </FormControl>
                <FormControl id="first_name">
                  <FormLabel>First Name</FormLabel>
                  <Input
                    type="text"
                    name="first_name"
                    value={formData.first_name}
                    onChange={handleFormChange}
                  />
                </FormControl>
                <FormControl id="last_name">
                  <FormLabel>Last Name</FormLabel>
                  <Input
                    type="text"
                    name="last_name"
                    value={formData.last_name}
                    onChange={handleFormChange}
                  />
                </FormControl>
                <FormControl id="subscribed">
                  <Checkbox
                    name="subscribed"
                    isChecked={formData.subscribed}
                    onChange={handleFormChange}
                  >
                    Subscribed
                  </Checkbox>
                </FormControl>
                <FormControl id="tags">
                  <FormLabel>Tags</FormLabel>
                  <Select
                    name="tag_ids"
                    placeholder="Select tags"
                    multiple
                    value={formData.tag_ids}
                    onChange={handleTagChange}
                  >
                    {tags.map((tag) => (
                      <option key={tag.id} value={tag.id}>
                        {tag.name}
                      </option>
                    ))}
                  </Select>
                </FormControl>
              </VStack>
            </ModalBody>
            <ModalFooter>
              <Button variant="ghost" onClick={() => setIsModalOpen(false)} mr={3}>
                Cancel
              </Button>
              <Button colorScheme="primary" type="submit">
                {currentContact ? 'Update' : 'Create'}
              </Button>
            </ModalFooter>
          </form>
        </ModalContent>
      </Modal>

      {/* Tag Management Modal */}
      <Modal isOpen={isTagModalOpen} onClose={() => setIsTagModalOpen(false)}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>Manage Tags</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            <VStack spacing={4}>
              <form onSubmit={handleTagFormSubmit} style={{ width: '100%' }}>
                <HStack width="100%">
                  <FormControl flex="1">
                    <FormLabel>Tag Name</FormLabel>
                    <Input
                      type="text"
                      name="name"
                      value={tagFormData.name}
                      onChange={handleTagFormChange}
                      placeholder="New Tag Name"
                      isRequired
                    />
                  </FormControl>
                  <Button type="submit" colorScheme="primary" mt="auto">
                    {currentTag ? 'Update Tag' : 'Add Tag'}
                  </Button>
                </HStack>
              </form>

              <Heading size="md" mt={6}>Existing Tags</Heading>
              {tags.length === 0 ? (
                <Text>No tags created yet.</Text>
              ) : (
                <Table variant="simple" size="sm">
                  <Thead>
                    <Tr>
                      <Th>Name</Th>
                      <Th>Actions</Th>
                    </Tr>
                  </Thead>
                  <Tbody>
                    {tags.map((tag) => (
                      <Tr key={tag.id}>
                        <Td>{tag.name}</Td>
                        <Td>
                          <HStack spacing={2}>
                            <Button size="xs" leftIcon={<EditIcon />} onClick={() => handleEditTag(tag)}>
                              Edit
                            </Button>
                            <Button size="xs" leftIcon={<DeleteIcon />} colorScheme="red" onClick={() => handleDeleteTag(tag.id)}>
                              Delete
                            </Button>
                          </HStack>
                        </Td>
                      </Tr>
                    ))}
                  </Tbody>
                </Table>
              )}
            </VStack>
          </ModalBody>
          <ModalFooter>
            <Button variant="ghost" onClick={() => setIsTagModalOpen(false)}>
              Close
            </Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </Box>
  );
};

export default ContactsPage;
</action>

