        <Table variant="simple">
          <Thead>
            <Tr>
              <Th>Title</Th>
              <Th>Published</Th>
              <Th>Created At</Th>
              <Th>Actions</Th>
            </Tr>
          </Thead>
          <Tbody>
            {blogPosts.map((post) => (
              <Tr key={post.id}>
                <Td>{post.title}</Td>
                <Td>{post.published === 1 ? 'Yes' : 'No'}</Td>
                <Td>{new Date(post.created_at).toLocaleDateString()}</Td>
                <Td>
                  <HStack spacing={2}>
