import React, { useEffect, useState } from 'react';
import {
  Box,
  Heading,
  VStack,
  FormControl,
  FormLabel,
  Input,
  Checkbox,
  Button,
  Flex,
  Spacer,
  useToast,
  Spinner,
  Alert,
  AlertIcon,
  Text,
} from '@chakra-ui/react';
import { useNavigate, useParams } from 'react-router-dom';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { api } from '../services/api';
// eslint-disable-next-line @typescript-eslint/no-unused-vars
import { AxiosError } from 'axios';

// Define a type for the expected API error response
interface ApiErrorResponse {
  detail: string;
}

interface BlogPost {
  id: number;
  title: string;
  content: string;
  published: number;
  created_at: string;
  updated_at?: string;
}

interface BlogPostFormData {
  title: string;
  content: string;
  published: boolean;
}

const BlogPostEditorPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const toast = useToast();

  const [formData, setFormData] = useState<BlogPostFormData>({
    title: '',
    content: '',
    published: true,
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [isSaving, setIsSaving] = useState(false);

  useEffect(() => {
    if (id) {
      setLoading(true);
      api.get<BlogPost>(`/blog-posts/${id}`)
        .then(response => {
          setFormData({
            title: response.data.title,
            content: response.data.content,
            published: response.data.published === 1,
          });
        })
        .catch((err: AxiosError) => {
          // Explicitly check for AxiosError to satisfy ESLint's unused import rule
          if (err.isAxiosError && err.response) {
            const errorDetail = (err.response.data as ApiErrorResponse)?.detail || 'Failed to load blog post.';
            setError(errorDetail);
            toast({
              title: 'Error',
              description: errorDetail,
              status: 'error',
              duration: 5000,
              isClosable: true,
            });
          } else {
            setError('An unexpected error occurred.');
            toast({
              title: 'Error',
              description: 'An unexpected error occurred.',
              status: 'error',
              duration: 5000,
              isClosable: true,
            });
          }
        })
        .finally(() => setLoading(false));
    }
  }, [id, toast]);

  const handleFormChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleQuillChange = (content: string) => {
    setFormData((prev) => ({ ...prev, content: content }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSaving(true);
    try {
      const payload = {
        ...formData,
        published: formData.published ? 1 : 0,
      };

      if (id) {
        await api.put(`/blog-posts/${id}`, payload);
        toast({
          title: 'Blog Post updated.',
          status: 'success',
          duration: 3000,
          isClosable: true,
        });
      } else {
        await api.post('/blog-posts/', payload);
        toast({
          title: 'Blog Post created.',
          status: 'success',
          duration: 3000,
          isClosable: true,
        });
      }
      navigate('/blog-posts'); // Go back to blog posts list
    } catch (err: AxiosError) {
      if (err.isAxiosError && err.response) {
        const errorDetail = (err.response.data as ApiErrorResponse)?.detail || 'Failed to save blog post.';
        toast({
          title: 'Error',
          description: errorDetail,
          status: 'error',
          duration: 5000,
          isClosable: true,
        });
      } else {
        toast({
          title: 'Error',
          description: 'An unexpected error occurred.',
          status: 'error',
          duration: 5000,
          isClosable: true,
        });
      }
    } finally {
      setIsSaving(false);
    }
  };

  if (loading) {
    return (
      <Box p={5} textAlign="center">
        <Spinner size="xl" />
        <Text mt={4}>Loading blog post...</Text>
      </Box>
    );
  }

  if (error) {
    return (
      <Box p={5}>
        <Alert status="error">
          <AlertIcon />
          {error}
        </Alert>
      </Box>
    );
  }

  return (
    <Box p={5} maxWidth="1000px" mx="auto">
      <Flex mb={4} alignItems="center">
        <Heading size="lg">{id ? 'Edit Blog Post' : 'Create New Blog Post'}</Heading>
        <Spacer />
        <Button variant="ghost" onClick={() => navigate('/blog-posts')} mr={2}>
          Cancel
        </Button>
        <Button colorScheme="primary" onClick={handleSubmit} isLoading={isSaving}>
          {id ? 'Update Post' : 'Create Post'}
        </Button>
      </Flex>

      <VStack spacing={4} as="form" onSubmit={handleSubmit}>
        <FormControl id="title" isRequired>
          <FormLabel>Title</FormLabel>
          <Input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleFormChange}
            placeholder="Enter blog post title"
          />
        </FormControl>
        <FormControl id="content">
          <FormLabel>Content</FormLabel>
          <Box minH="400px" width="100%"> {/* Removed border and borderRadius */}
            <ReactQuill
              theme="snow"
              value={formData.content}
              onChange={handleQuillChange}
              style={{ height: '350px' }}
            />
          </Box>
        </FormControl>
        <FormControl id="published">
          <Checkbox
            name="published"
            isChecked={formData.published}
            onChange={handleFormChange}
          >
            Published
          </Checkbox>
        </FormControl>
      </VStack>
    </Box>
  );
};

export default BlogPostEditorPage;
</action>

