from sqlalchemy.orm import Session
from db_config import get_db
from database.todo import Todo
from models.todo_models import TodoCreate, TodoRead, TodoUpdate
from typing import List

router = APIRouter(prefix="/todos", tags=["todos"])

@router.post("/", response_model=TodoRead, status_code=status.HTTP_201_CREATED)
def create_todo(todo: TodoCreate, db: Session = Depends(get_db)):
    print("Creating todo:", todo.title)
    db_todo = Todo(title=todo.title, description=todo.description)
    db.add(db_todo)
    db.commit()
    db.refresh(db_todo)
    return TodoRead.model_validate(db_todo)

@router.get("/", response_model=List[TodoRead])
def list_todos(db: Session = Depends(get_db)):
    print("Listing todos")
    todos = db.query(Todo).all()
    return [TodoRead.model_validate(todo) for todo in todos]

@router.get("/{todo_id}/", response_model=TodoRead)
def get_todo(todo_id: int, db: Session = Depends(get_db)):
    print("Getting todo:", todo_id)
    todo = db.query(Todo).filter(Todo.id == todo_id).first()
    if not todo:
        raise HTTPException(status_code=404, detail="Todo not found")
    return TodoRead.model_validate(todo)

@router.put("/{todo_id}/", response_model=TodoRead)
def update_todo(todo_id: int, todo_update: TodoUpdate, db: Session = Depends(get_db)):
    print("Updating todo:", todo_id)
    todo = db.query(Todo).filter(Todo.id == todo_id).first()
    if not todo:
        raise HTTPException(status_code=404, detail="Todo not found")
    if todo_update.title is not None:
        todo.title = todo_update.title
    if todo_update.description is not None:
        todo.description = todo_update.description
    if todo_update.completed is not None:
        todo.completed = todo_update.completed
    db.commit()
    db.refresh(todo)
    return TodoRead.model_validate(todo)

@router.delete("/{todo_id}/", status_code=status.HTTP_204_NO_CONTENT)
def delete_todo(todo_id: int, db: Session = Depends(get_db)):
    print("Deleting todo:", todo_id)
