import React, { useEffect, useState } from 'react';
import {
  Box, Heading, VStack, HStack, Input, Button, Checkbox, Text, Spinner, useToast, IconButton, Badge,
} from '@chakra-ui/react';
import { AddIcon, DeleteIcon, EditIcon, CheckIcon } from '@chakra-ui/icons';
import { getTodos, createTodo, updateTodo, deleteTodo } from '../api/todoApi';
import { Todo, TodoCreate, TodoUpdate } from '../types/todo';

const TodoPage: React.FC = () => {
  const [todos, setTodos] = useState<Todo[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [newTitle, setNewTitle] = useState('');
  const [newDescription, setNewDescription] = useState('');
  const [editingId, setEditingId] = useState<number | null>(null);
  const [editTitle, setEditTitle] = useState('');
  const [editDescription, setEditDescription] = useState('');
  const toast = useToast();

  const fetchTodos = async () => {
    setLoading(true);
    try {
      const data = await getTodos();
      setTodos(data);
      setError(null);
    } catch (err: any) {
      setError(err.message);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  const handleAddTodo = async () => {
    if (!newTitle.trim()) {
      toast({ title: 'Title is required', status: 'warning', duration: 2000 });
      return;
    }
    try {
      await createTodo({ title: newTitle, description: newDescription });
      setNewTitle('');
      setNewDescription('');
      fetchTodos();
      toast({ title: 'Todo added', status: 'success', duration: 1500 });
    } catch {
      toast({ title: 'Failed to add todo', status: 'error', duration: 2000 });
    }
  };

  const handleEditTodo = async (id: number) => {
    try {
      await updateTodo(id, { title: editTitle, description: editDescription });
      setEditingId(null);
      fetchTodos();
      toast({ title: 'Todo updated', status: 'success', duration: 1500 });
    } catch {
      toast({ title: 'Failed to update todo', status: 'error', duration: 2000 });
    }
  };

  const handleToggleComplete = async (id: number, completed: boolean) => {
    try {
      await updateTodo(id, { completed: !completed });
      fetchTodos();
      toast({ title: 'Todo status updated', status: 'success', duration: 1500 });
    } catch {
      toast({ title: 'Failed to update status', status: 'error', duration: 2000 });
    }
  };

  const handleDeleteTodo = async (id: number) => {
    try {
      await deleteTodo(id);
      fetchTodos();
      toast({ title: 'Todo deleted', status: 'info', duration: 1500 });
    } catch {
      toast({ title: 'Failed to delete todo', status: 'error', duration: 2000 });
    }
  };

  return (
    <Box maxW="600px" mx="auto" mt={8} p={4} bg="gray.50" borderRadius="md" boxShadow="md">
      <Heading mb={4}>Todo List</Heading>
      <VStack spacing={4} align="stretch">
        <Box>
          <HStack>
            <Input
              placeholder="Title"
              value={newTitle}
              onChange={e => setNewTitle(e.target.value)}
              flex={2}
            />
            <Input
              placeholder="Description"
              value={newDescription}
              onChange={e => setNewDescription(e.target.value)}
              flex={3}
            />
            <Button leftIcon={<AddIcon />} colorScheme="teal" onClick={handleAddTodo}>
              Add
            </Button>
          </HStack>
        </Box>
        {loading ? (
          <Spinner size="lg" alignSelf="center" />
        ) : error ? (
          <Text color="red.500">{error}</Text>
        ) : todos.length === 0 ? (
          <Text color="gray.500">No todos yet. Add your first one!</Text>
        ) : (
          todos.map(todo =>
            editingId === todo.id ? (
              <Box key={todo.id} p={3} bg="white" borderRadius="md" boxShadow="sm">
                <HStack>
                  <Input
                    value={editTitle}
                    onChange={e => setEditTitle(e.target.value)}
                    placeholder="Edit title"
                  />
                  <Input
                    value={editDescription}
                    onChange={e => setEditDescription(e.target.value)}
                    placeholder="Edit description"
                  />
                  <IconButton
                    aria-label="Save"
                    icon={<CheckIcon />}
                    colorScheme="green"
                    onClick={() => handleEditTodo(todo.id)}
                  />
                  <IconButton
                    aria-label="Cancel"
                    icon={<DeleteIcon />}
                    colorScheme="red"
                    onClick={() => setEditingId(null)}
                  />
                </HStack>
              </Box>
            ) : (
              <Box key={todo.id} p={3} bg="white" borderRadius="md" boxShadow="sm">
                <HStack>
                  <Checkbox
                    isChecked={todo.completed}
                    onChange={() => handleToggleComplete(todo.id, todo.completed)}
                    colorScheme="teal"
                  />
                  <VStack align="start" spacing={0} flex={1}>
                    <Text fontWeight="bold">{todo.title}</Text>
                    <Text fontSize="sm" color="gray.600">{todo.description}</Text>
                  </VStack>
                  <Badge colorScheme={todo.completed ? "green" : "red"}>
                    {todo.completed ? "Completed" : "Pending"}
                  </Badge>
                  <IconButton
                    aria-label="Edit"
                    icon={<EditIcon />}
                    colorScheme="blue"
                    onClick={() => {
                      setEditingId(todo.id);
                      setEditTitle(todo.title);
                      setEditDescription(todo.description || '');
                    }}
                  />
                  <IconButton
                    aria-label="Delete"
                    icon={<DeleteIcon />}
                    colorScheme="red"
                    onClick={() => handleDeleteTodo(todo.id)}
                  />
                </HStack>
              </Box>
            )
          )
        )}
      </VStack>
    </Box>
  );
};

export default TodoPage;
