import { Task } from "../types/task";

const API_URL = import.meta.env.VITE_API_URL || "http://localhost:8003";

export async function fetchTasks(): Promise<Task[]> {
  const res = await fetch(`${API_URL}/tasks/`);
  if (!res.ok) throw new Error("Failed to fetch tasks");
  return await res.json();
}

export async function createTask(data: Omit<Task, "id" | "completed">): Promise<Task> {
  const res = await fetch(`${API_URL}/tasks/`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(data),
  });
  if (!res.ok) throw new Error("Failed to create task");
  return await res.json();
}

export async function updateTask(id: number, updates: Partial<Task>): Promise<Task> {
  const res = await fetch(`${API_URL}/tasks/${id}/`, {
    method: "PUT",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(updates),
  });
  if (!res.ok) throw new Error("Failed to update task");
  return await res.json();
}

export async function deleteTask(id: number): Promise<void> {
  const res = await fetch(`${API_URL}/tasks/${id}/`, {
    method: "DELETE",
  });
  if (!res.ok) throw new Error("Failed to delete task");
}
</action>

Now I'll build the main shadcn/ui components for the todo app, starting with TaskCard, TaskForm, and TaskList. These will provide a professional, modern interface for managing tasks.

**Step 1: TaskCard component**
Displays a single task, with complete/incomplete toggle, edit, and delete actions.

<action type="file" filePath="frontend/src/components/TaskCard.tsx">
import { Task } from "../types/task";
import { useTaskStore } from "../stores/task-store";
import { Card } from "./ui/card";
import { Checkbox } from "./ui/checkbox";
import { Button } from "./ui/button";
import { Badge } from "./ui/badge";
import { toast } from "sonner";

interface TaskCardProps {
  task: Task;
  onEdit: (task: Task) => void;
}

export function TaskCard({ task, onEdit }: TaskCardProps) {
  const { updateTask, deleteTask, loading } = useTaskStore();

  const handleComplete = async () => {
    await updateTask(task.id, { completed: !task.completed });
    toast.success(task.completed ? "Marked incomplete" : "Task completed!");
  };

  const handleDelete = async () => {
    await deleteTask(task.id);
    toast.success("Task deleted");
  };

  return (
    <Card className="flex items-center justify-between p-4 mb-2 shadow-sm">
      <div className="flex items-center gap-3">
        <Checkbox checked={task.completed} onCheckedChange={handleComplete} />
        <div>
          <div className={`font-semibold ${task.completed ? "line-through text-accent" : ""}`}>
            {task.title}
          </div>
          {task.description && (
            <div className="text-muted-foreground text-sm">{task.description}</div>
          )}
          <div className="flex gap-2 mt-1">
            {task.due_date && (
              <Badge variant="secondary">
                Due: {new Date(task.due_date).toLocaleDateString()}
              </Badge>
            )}
            {task.priority && (
              <Badge variant={task.priority === "High" ? "destructive" : "secondary"}>
                {task.priority}
              </Badge>
            )}
            {task.category && (
              <Badge variant="outline">{task.category}</Badge>
            )}
          </div>
        </div>
      </div>
      <div className="flex gap-2">
        <Button
          variant="outline"
          size="sm"
          onClick={() => onEdit(task)}
          disabled={loading}
        >
          Edit
        </Button>
        <Button
          variant="destructive"
          size="sm"
          onClick={handleDelete}
          disabled={loading}
        >
          Delete
        </Button>
      </div>
    </Card>
  );
}
</action>

Next, I'll create the TaskForm component for adding and editing tasks using shadcn/ui Dialog, Input, Select, and Button components.

<action type="file" filePath="frontend/src/components/TaskForm.tsx">
import { useState } from "react";
import { Task } from "../types/task";
import { useTaskStore } from "../stores/task-store";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "./ui/dialog";
import { Input } from "./ui/input";
import { Button } from "./ui/button";
import { Select, SelectItem } from "./ui/select";
import { Label } from "./ui/label";
import { toast } from "sonner";

interface TaskFormProps {
  open: boolean;
  onClose: () => void;
  initial?: Partial<Task>;
}

const priorities = ["Low", "Medium", "High"];

export function TaskForm({ open, onClose, initial }: TaskFormProps) {
  const { addTask, updateTask, loading } = useTaskStore();
  const isEdit = !!initial?.id;

  const [title, setTitle] = useState(initial?.title || "");
  const [description, setDescription] = useState(initial?.description || "");
  const [dueDate, setDueDate] = useState(initial?.due_date || "");
  const [priority, setPriority] = useState(initial?.priority || "");
  const [category, setCategory] = useState(initial?.category || "");

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!title.trim()) {
      toast.error("Title is required");
      return;
    }
    const data = {
      title,
      description,
      due_date: dueDate || undefined,
      priority: priority || undefined,
      category: category || undefined,
    };
    if (isEdit && initial?.id) {
      await updateTask(initial.id, data);
      toast.success("Task updated");
    } else {
      await addTask(data);
      toast.success("Task added");
    }
    onClose();
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent>
        <DialogHeader>
          <DialogTitle>{isEdit ? "Edit Task" : "Add Task"}</DialogTitle>
        </DialogHeader>
        <form className="space-y-4" onSubmit={handleSubmit}>
          <div>
            <Label htmlFor="title">Title</Label>
            <Input
              id="title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
              disabled={loading}
            />
          </div>
          <div>
            <Label htmlFor="description">Description</Label>
            <Input
              id="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              disabled={loading}
            />
          </div>
          <div className="flex gap-2">
            <div className="flex-1">
              <Label htmlFor="due_date">Due Date</Label>
              <Input
                id="due_date"
                type="date"
                value={dueDate}
                onChange={(e) => setDueDate(e.target.value)}
                disabled={loading}
              />
            </div>
            <div className="flex-1">
              <Label htmlFor="priority">Priority</Label>
              <Select
                id="priority"
                value={priority}
                onValueChange={setPriority}
                disabled={loading}
              >
                <SelectItem value="">None</SelectItem>
                {priorities.map((p) => (
                  <SelectItem key={p} value={p}>
                    {p}
                  </SelectItem>
                ))}
              </Select>
            </div>
            <div className="flex-1">
              <Label htmlFor="category">Category</Label>
              <Input
                id="category"
                value={category}
                onChange={(e) => setCategory(e.target.value)}
                disabled={loading}
              />
            </div>
          </div>
          <div className="flex justify-end gap-2">
            <Button type="button" variant="outline" onClick={onClose} disabled={loading}>
              Cancel
            </Button>
            <Button type="submit" disabled={loading}>
              {isEdit ? "Update" : "Add"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}
</action>

Now I'll create the TaskList component, which displays all tasks, supports filtering (Today, Upcoming, Completed, Category), and integrates TaskCard and TaskForm.

<action type="file" filePath="frontend/src/components/TaskList.tsx">
import { useState, useEffect } from "react";
import { useTaskStore } from "../stores/task-store";
import { Task } from "../types/task";
import { TaskCard } from "./TaskCard";
import { TaskForm } from "./TaskForm";
import { Button } from "./ui/button";
import { Separator } from "./ui/separator";
import { Badge } from "./ui/badge";

type FilterType = "all" | "today" | "upcoming" | "completed" | "category";

export function TaskList() {
  const { tasks, fetchAll, loading, error } = useTaskStore();
  const [showForm, setShowForm] = useState(false);
  const [editTask, setEditTask] = useState<Task | null>(null);
  const [filter, setFilter] = useState<FilterType>("all");
  const [category, setCategory] = useState<string>("");

  useEffect(() => {
    fetchAll();
    // eslint-disable-next-line
  }, []);

  const today = new Date().toISOString().slice(0, 10);

  let filtered = tasks;
  if (filter === "today") {
    filtered = tasks.filter((t) => t.due_date?.slice(0, 10) === today);
  } else if (filter === "upcoming") {
    filtered = tasks.filter(
      (t) => t.due_date && t.due_date.slice(0, 10) > today && !t.completed
    );
  } else if (filter === "completed") {
    filtered = tasks.filter((t) => t.completed);
  } else if (filter === "category" && category) {
    filtered = tasks.filter((t) => t.category === category);
  }

  // Collect unique categories for sidebar/filter
  const categories = Array.from(new Set(tasks.map((t) => t.category).filter(Boolean)));

  return (
    <div className="max-w-2xl mx-auto py-6">
      <div className="flex justify-between items-center mb-4">
        <div className="flex gap-2">
          <Button
            variant={filter === "all" ? "default" : "outline"}
            onClick={() => setFilter("all")}
          >
            All
          </Button>
          <Button
            variant={filter === "today" ? "default" : "outline"}
            onClick={() => setFilter("today")}
          >
            Today
          </Button>
          <Button
            variant={filter === "upcoming" ? "default" : "outline"}
            onClick={() => setFilter("upcoming")}
          >
            Upcoming
          </Button>
          <Button
            variant={filter === "completed" ? "default" : "outline"}
            onClick={() => setFilter("completed")}
          >
            Completed
          </Button>
          {categories.length > 0 && (
            <div className="flex gap-1 items-center">
              <Separator orientation="vertical" />
              <span className="text-sm text-muted-foreground">Category:</span>
              <select
                className="border rounded px-2 py-1"
                value={category}
                onChange={(e) => {
                  setCategory(e.target.value);
                  setFilter("category");
                }}
              >
                <option value="">Select</option>
                {categories.map((cat) => (
                  <option key={cat} value={cat}>
                    {cat}
                  </option>
                ))}
              </select>
            </div>
          )}
        </div>
        <Button onClick={() => { setShowForm(true); setEditTask(null); }}>
          + Add Task
        </Button>
      </div>
      <Separator />
      {loading && <div className="mt-6 text-center text-muted-foreground">Loading tasks...</div>}
      {error && <div className="mt-6 text-center text-destructive">{error}</div>}
      {!loading && filtered.length === 0 && (
        <div className="mt-6 text-center text-muted-foreground">
          <Badge variant="outline">No tasks found</Badge>
        </div>
      )}
      <div className="mt-4">
        {filtered.map((task) => (
          <TaskCard key={task.id} task={task} onEdit={(t) => { setEditTask(t); setShowForm(true); }} />
        ))}
      </div>
      <TaskForm
        open={showForm}
        onClose={() => { setShowForm(false); setEditTask(null); }}
        initial={editTask || undefined}
      />
    </div>
  );
}
</action>

Updating `App.tsx` to remove authentication and protected routes (since not needed for a solo todo app), and render the TaskList directly as the main page.

